import React, { useState, useRef, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const menuRef = useRef();
  const sidebarRef = useRef();

  // Close menu on outside click
  useEffect(() => {
    if (!menuOpen) return;
    function handleClick(e) {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    }
    document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [menuOpen]);

  // Close sidebar on outside click
  useEffect(() => {
    if (!sidebarOpen) return;
    function handleSidebarClick(e) {
      if (sidebarRef.current && !sidebarRef.current.contains(e.target) && !e.target.classList.contains('sidebar-toggle')) {
        setSidebarOpen(false);
      }
    }
    document.addEventListener('mousedown', handleSidebarClick);
    return () => document.removeEventListener('mousedown', handleSidebarClick);
  }, [sidebarOpen]);

  const handleLogout = () => {
    logout();
    setMenuOpen(false);
    setSidebarOpen(false);
    navigate('/signup');
  };

  const handleNavClick = (to) => {
    setMenuOpen(false);
    setSidebarOpen(false);
    navigate(to);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        {/* Sidebar Hamburger for mobile */}
        <button
          className="sidebar-toggle"
          aria-label="Toggle sidebar navigation"
          aria-expanded={sidebarOpen}
          onClick={() => setSidebarOpen(open => !open)}
          style={{ display: 'inline-flex', alignItems: 'center', background: 'none', border: 'none', marginRight: 12, fontSize: 24, cursor: 'pointer' }}
        >
          <span style={{ display: 'block', width: 24, height: 3, background: '#1976d2', margin: '4px 0', borderRadius: 2 }} />
          <span style={{ display: 'block', width: 24, height: 3, background: '#1976d2', margin: '4px 0', borderRadius: 2 }} />
          <span style={{ display: 'block', width: 24, height: 3, background: '#1976d2', margin: '4px 0', borderRadius: 2 }} />
        </button>
        <span className="navbar-logo" onClick={() => handleNavClick('/dashboard')} style={{ cursor: 'pointer', fontWeight: 700, fontSize: '1.3rem', color: '#1976d2' }}>
          ðŸš¢ ShipMaster
        </span>
      </div>
      <div className="navbar-right">
        <button
          className="navbar-toggle"
          aria-label="Toggle navigation menu"
          onClick={() => setMenuOpen((open) => !open)}
        >
          <span style={{ display: 'block', width: 22, height: 2, background: '#1976d2', margin: '4px 0', borderRadius: 2 }} />
          <span style={{ display: 'block', width: 22, height: 2, background: '#1976d2', margin: '4px 0', borderRadius: 2 }} />
          <span style={{ display: 'block', width: 22, height: 2, background: '#1976d2', margin: '4px 0', borderRadius: 2 }} />
        </button>
        <div className={`navbar-links${menuOpen ? ' open' : ''}`} ref={menuRef}>
          {user && (
            <>
              <span onClick={() => handleNavClick('/dashboard')} className="nav-link" style={{ cursor: 'pointer' }}>Dashboard</span>
              <span onClick={() => handleNavClick('/ships')} className="nav-link" style={{ cursor: 'pointer' }}>Ship Management</span>
              <span onClick={() => handleNavClick('/jobs')} className="nav-link" style={{ cursor: 'pointer' }}>Jobs</span>
            </>
          )}
          <button onClick={toggleTheme} className="nav-btn">
            {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
          </button>
          {user && (
            <button onClick={handleLogout} className="nav-btn">Logout</button>
          )}
        </div>
      </div>
      {/* Sidebar overlay for mobile */}
      <div
        id="sidebar"
        ref={sidebarRef}
        className={`sidebar${sidebarOpen ? ' open' : ''}`}
        style={{
          display: sidebarOpen ? 'flex' : 'none',
          position: 'fixed',
          left: 0,
          top: 0,
          height: '100vh',
          width: 230,
          background: 'var(--sidebar-bg, #1a2238)',
          color: 'var(--sidebar-link, #e0e6ed)',
          flexDirection: 'column',
          zIndex: 9999,
          transition: 'transform 0.3s',
          transform: sidebarOpen ? 'translateX(0)' : 'translateX(-100%)',
          boxShadow: '2px 0 12px rgba(30, 64, 175, 0.13)',
          padding: '2rem 1rem 1rem 1.5rem',
        }}
      >
        <span className="sidebar-logo" style={{ color: 'var(--sidebar-link-active, #fff)', fontWeight: 700, fontSize: '1.3rem', marginBottom: 24 }}>ðŸš¢ ShipMaster</span>
        <NavLink to="/dashboard" className="sidebar-link" onClick={() => setSidebarOpen(false)}>Dashboard</NavLink>
        <NavLink to="/ships" className="sidebar-link" onClick={() => setSidebarOpen(false)}>Ship Management</NavLink>
        <NavLink to="/jobs" className="sidebar-link" onClick={() => setSidebarOpen(false)}>Jobs</NavLink>
        <button onClick={toggleTheme} className="sidebar-link" style={{ marginTop: 16 }}>{theme === 'light' ? 'Dark Mode' : 'Light Mode'}</button>
        {user && <button onClick={handleLogout} className="sidebar-link" style={{ marginTop: 8 }}>Logout</button>}
      </div>
    </nav>
  );
};

export default Navbar;
