import React, { useState, useEffect } from 'react';
import { JobsStatusChart, MaintenanceStatusChart } from '../components/Dashboard/Charts';
import KPICards from '../components/Dashboard/KPICards';
import './DashboardPage.css';

const DashboardPage = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [showMobileFilters, setShowMobileFilters] = useState(false);
  const [filters, setFilters] = useState({
    timeRange: 'month',
    shipType: 'all',
    priority: 'all'
  });

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Simulate API data fetching
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        
        await new Promise(resolve => setTimeout(resolve, 800));
        
        const mockData = {
          stats: {
            totalShips: 12,
            overdueMaintenance: 4,
            jobsInProgress: 6,
            jobsCompleted: 18,
            highPriorityJobs: 3
          },
          charts: {
            jobsStatus: { inProgress: 6, completed: 18 },
            maintenanceStatus: { overdue: 4, onSchedule: 8 }
          },
          recentActivities: [
            {
              id: 1,
              ship: "Ocean Voyager",
              component: "Engine",
              action: "Routine inspection",
              status: "in-progress",
              priority: "high",
              timestamp: "2025-05-12T10:30:00"
            },
            {
              id: 2,
              ship: "Pacific Star",
              component: "Navigation",
              action: "GPS calibration",
              status: "completed",
              priority: "medium",
              timestamp: "2025-05-11T14:15:00"
            }
          ],
          ships: ["Ocean Voyager", "Pacific Star", "Atlantic Explorer"]
        };

        // Apply filters
        if (filters.timeRange === 'week') {
          mockData.stats.jobsInProgress = 3;
          mockData.stats.jobsCompleted = 7;
          mockData.charts.jobsStatus.inProgress = 3;
          mockData.charts.jobsStatus.completed = 7;
        }

        if (filters.priority === 'high') {
          mockData.recentActivities = mockData.recentActivities.filter(
            activity => activity.priority === 'high'
          );
        }

        setDashboardData(mockData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [filters]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
    if (isMobile) setShowMobileFilters(false);
  };

  const formatTimeAgo = (timestamp) => {
    const now = new Date();
    const date = new Date(timestamp);
    const diffHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffHours < 24) return `${diffHours} ${diffHours === 1 ? 'hour' : 'hours'} ago`;
    const days = Math.floor(diffHours / 24);
    return `${days} ${days === 1 ? 'day' : 'days'} ago`;
  };

  if (loading) return <div className="loading-spinner">Loading Dashboard...</div>;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Ship Management Dashboard</h1>
        
        {isMobile ? (
          <button 
            className="mobile-filter-toggle"
            onClick={() => setShowMobileFilters(!showMobileFilters)}
          >
            {showMobileFilters ? 'Hide Filters' : 'Show Filters'}
          </button>
        ) : (
          <div className="dashboard-filters">
            <FilterGroup 
              label="Time Range"
              name="timeRange"
              value={filters.timeRange}
              onChange={handleFilterChange}
              options={[
                { value: 'week', label: 'Last Week' },
                { value: 'month', label: 'Last Month' },
                { value: 'quarter', label: 'Last Quarter' }
              ]}
            />
            
            <FilterGroup 
              label="Ship"
              name="shipType"
              value={filters.shipType}
              onChange={handleFilterChange}
              options={[
                { value: 'all', label: 'All Ships' },
                ...(dashboardData?.ships.map(ship => ({
                  value: ship.toLowerCase().replace(' ', '-'),
                  label: ship
                })) || [])
              ]}
            />
            
            <FilterGroup 
              label="Priority"
              name="priority"
              value={filters.priority}
              onChange={handleFilterChange}
              options={[
                { value: 'all', label: 'All Priorities' },
                { value: 'high', label: 'High' },
                { value: 'medium', label: 'Medium' },
                { value: 'low', label: 'Low' }
              ]}
            />
          </div>
        )}
      </div>

      {isMobile && showMobileFilters && (
        <div className="mobile-filters">
          <FilterGroup 
            label="Time Range"
            name="timeRange"
            value={filters.timeRange}
            onChange={handleFilterChange}
            options={[
              { value: 'week', label: 'Last Week' },
              { value: 'month', label: 'Last Month' },
              { value: 'quarter', label: 'Last Quarter' }
            ]}
          />
          
          <FilterGroup 
            label="Ship"
            name="shipType"
            value={filters.shipType}
            onChange={handleFilterChange}
            options={[
              { value: 'all', label: 'All Ships' },
              ...(dashboardData?.ships.map(ship => ({
                value: ship.toLowerCase().replace(' ', '-'),
                label: ship
              })) || [])
            ]}
          />
          
          <FilterGroup 
            label="Priority"
            name="priority"
            value={filters.priority}
            onChange={handleFilterChange}
            options={[
              { value: 'all', label: 'All Priorities' },
              { value: 'high', label: 'High' },
              { value: 'medium', label: 'Medium' },
              { value: 'low', label: 'Low' }
            ]}
          />
        </div>
      )}

      <KPICards 
        totalShips={dashboardData.stats.totalShips}
        overdueComponents={dashboardData.stats.overdueMaintenance}
        jobsInProgress={dashboardData.stats.jobsInProgress}
        jobsCompleted={dashboardData.stats.jobsCompleted}
        isMobile={isMobile}
      />

      <div className="charts-container">
        <div className="chart-card">
          <h3>Jobs Status</h3>
          <div className="chart-wrapper">
            <JobsStatusChart 
              inProgress={dashboardData.charts.jobsStatus.inProgress}
              completed={dashboardData.charts.jobsStatus.completed}
              isMobile={isMobile}
            />
          </div>
        </div>
        
        <div className="chart-card">
          <h3>Maintenance Status</h3>
          <div className="chart-wrapper">
            <MaintenanceStatusChart 
              overdue={dashboardData.charts.maintenanceStatus.overdue}
              totalShips={dashboardData.stats.totalShips}
              isMobile={isMobile}
            />
          </div>
        </div>
      </div>

      <div className="recent-activity">
        <div className="section-header">
          <h2>Recent Activity</h2>
          <button 
            className="refresh-btn"
            onClick={() => window.location.reload()}
            aria-label="Refresh data"
          >
            ↻ Refresh
          </button>
        </div>
        
        <div className="activity-list">
          {dashboardData.recentActivities.map(activity => (
            <div 
              key={activity.id} 
              className={`activity-item ${activity.status} priority-${activity.priority}`}
            >
              <div className="activity-icon">
                {activity.status === 'completed' ? '✓' : '↻'}
              </div>
              
              <div className="activity-content">
                <div className="activity-header">
                  <span className="ship-name">{activity.ship}</span>
                  {!isMobile && (
                    <span className={`priority-badge ${activity.priority}`}>
                      {activity.priority}
                    </span>
                  )}
                </div>
                <p className="activity-desc">
                  {activity.component} - {activity.action}
                </p>
                <div className="activity-footer">
                  <span className="timestamp">
                    {formatTimeAgo(activity.timestamp)}
                  </span>
                  <button className="action-btn">
                    {activity.status === 'completed' ? 'View' : 'Update'}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

// Reusable Filter Group Component
const FilterGroup = ({ label, name, value, onChange, options }) => (
  <div className="filter-group">
    <label htmlFor={name}>{label}:</label>
    <select
      id={name}
      name={name}
      value={value}
      onChange={onChange}
      aria-label={label}
    >
      {options.map(option => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}
    </select>
  </div>
);

export default DashboardPage;
