import React, { useState } from 'react';

const MaintenanceJobs = () => {
  // Sample engineers data
  const engineers = [
    { id: 1, name: 'John Smith' },
    { id: 2, name: 'Sarah Johnson' },
    { id: 3, name: 'Michael Brown' }
  ];

  // Sample ships data
  const ships = [
    { id: 1, name: 'Ocean Voyager' },
    { id: 2, name: 'Marine Express' },
    { id: 3, name: 'Pacific Carrier' }
  ];

  // Sample components data
  const components = [
    { id: 1, name: 'Engine' },
    { id: 2, name: 'Propeller' },
    { id: 3, name: 'Navigation System' },
    { id: 4, name: 'Cooling System' }
  ];

  // Job types
  const jobTypes = ['Inspection', 'Repair', 'Maintenance', 'Replacement'];

  // State for jobs and form
  const [jobs, setJobs] = useState([
    {
      id: 1,
      ship: 'Ocean Voyager',
      component: 'Engine',
      type: 'Inspection',
      priority: 'High',
      status: 'In Progress',
      startDate: 'May 12, 2025',
      assignedTo: 'John Smith'
    }
  ]);

  const [formData, setFormData] = useState({
    ship: '',
    component: '',
    type: '',
    priority: 'Medium',
    assignedTo: ''
  });

  // Filter states
  const [filterShip, setFilterShip] = useState('All Ships');
  const [filterStatus, setFilterStatus] = useState('All Statuses');
  const [filterPriority, setFilterPriority] = useState('All Priorities');
  
  // Mobile view state
  const [showFilters, setShowFilters] = useState(false);

  // Create new job
  const createJob = (e) => {
    e.preventDefault();
    const newJob = {
      id: jobs.length + 1,
      ship: formData.ship,
      component: formData.component,
      type: formData.type,
      priority: formData.priority,
      status: 'Pending',
      startDate: new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' }),
      assignedTo: formData.assignedTo
    };
    setJobs([...jobs, newJob]);
    setFormData({
      ship: '',
      component: '',
      type: '',
      priority: 'Medium',
      assignedTo: ''
    });
  };

  // Update job status
  const updateJobStatus = (jobId, newStatus) => {
    setJobs(jobs.map(job => 
      job.id === jobId ? { ...job, status: newStatus } : job
    ));
  };

  // Filter jobs
  const filteredJobs = jobs.filter(job => {
    return (
      (filterShip === 'All Ships' || job.ship === filterShip) &&
      (filterStatus === 'All Statuses' || job.status === filterStatus) &&
      (filterPriority === 'All Priorities' || job.priority === filterPriority)
    );
  });

  return (
    <div className="p-4 md:p-5 lg:p-6 max-w-6xl mx-auto">
      <h1 className="text-2xl md:text-3xl font-bold mb-6 text-center md:text-left">Maintenance Jobs Management</h1>
      
      {/* Create Job Form */}
      <div className="mb-6 md:mb-8 p-4 md:p-6 border border-gray-200 rounded-lg shadow-sm">
        <h2 className="text-xl font-semibold mb-4">Create New Maintenance Job</h2>
        <form onSubmit={createJob}>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            <div className="mb-4">
              <label className="block mb-2 font-medium">Ship:</label>
              <select
                value={formData.ship}
                onChange={(e) => setFormData({...formData, ship: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="">Select Ship</option>
                {ships.map(ship => (
                  <option key={ship.id} value={ship.name}>{ship.name}</option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block mb-2 font-medium">Component:</label>
              <select
                value={formData.component}
                onChange={(e) => setFormData({...formData, component: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="">Select Component</option>
                {components.map(component => (
                  <option key={component.id} value={component.name}>{component.name}</option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block mb-2 font-medium">Job Type:</label>
              <select
                value={formData.type}
                onChange={(e) => setFormData({...formData, type: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="">Select Type</option>
                {jobTypes.map((type, index) => (
                  <option key={index} value={type}>{type}</option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block mb-2 font-medium">Priority:</label>
              <select
                value={formData.priority}
                onChange={(e) => setFormData({...formData, priority: e.target.value})}
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </select>
            </div>

            <div className="mb-4">
              <label className="block mb-2 font-medium">Assigned Engineer:</label>
              <select
                value={formData.assignedTo}
                onChange={(e) => setFormData({...formData, assignedTo: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="">Select Engineer</option>
                {engineers.map(engineer => (
                  <option key={engineer.id} value={engineer.name}>{engineer.name}</option>
                ))}
              </select>
            </div>
          </div>
          <button 
            type="submit"
            className="mt-4 px-4 py-2 bg-green-500 text-white font-medium rounded hover:bg-green-600 w-full sm:w-auto"
          >
            Create Job
          </button>
        </form>
      </div>

      {/* Jobs List with Filters */}
      <div>
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Maintenance Jobs</h2>
          <button 
            className="md:hidden px-3 py-1 bg-blue-500 text-white rounded text-sm"
            onClick={() => setShowFilters(!showFilters)}
          >
            {showFilters ? 'Hide Filters' : 'Show Filters'}
          </button>
        </div>
        
        <div className={`${showFilters ? 'block' : 'hidden'} md:block`}>
          <div className="flex flex-col md:flex-row gap-4 mb-6">
            <div className="flex-1">
              <label className="block mb-2 font-medium">Filter by Ship:</label>
              <select 
                value={filterShip} 
                onChange={(e) => setFilterShip(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="All Ships">All Ships</option>
                {ships.map(ship => (
                  <option key={ship.id} value={ship.name}>{ship.name}</option>
                ))}
              </select>
            </div>

            <div className="flex-1">
              <label className="block mb-2 font-medium">Filter by Status:</label>
              <select 
                value={filterStatus} 
                onChange={(e) => setFilterStatus(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="All Statuses">All Statuses</option>
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </div>

            <div className="flex-1">
              <label className="block mb-2 font-medium">Filter by Priority:</label>
              <select 
                value={filterPriority} 
                onChange={(e) => setFilterPriority(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded"
              >
                <option value="All Priorities">All Priorities</option>
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </select>
            </div>
          </div>
        </div>
        {/* Responsive Calendar View */}
        <div className="mb-8">
          <h3 className="text-lg font-medium mb-2">Maintenance Calendar</h3>
          <div className="grid grid-cols-7 gap-1 text-sm sm:text-base">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="text-center font-bold p-1 sm:p-2 bg-gray-100">
                {window.innerWidth < 640 ? day.slice(0, 1) : day}
              </div>
            ))}
            {Array.from({ length: 35 }, (_, i) => {
              const date = new Date();
              date.setDate(i + 1);
              const dayJobs = jobs.filter(job => new Date(job.startDate).getDate() === date.getDate());
              
              return (
                <div
                  key={i}
                  className={`p-1 sm:p-2 border border-gray-200 min-h-[40px] sm:min-h-[60px] md:min-h-[80px] cursor-pointer transition-colors hover:bg-gray-50 ${dayJobs.length ? 'bg-gray-50' : 'bg-white'}`}
                  onClick={() => {
                    if (dayJobs.length) {
                      alert(`Jobs on ${date.toLocaleDateString()}:\n${dayJobs.map(job => 
                        `- ${job.type} of ${job.component} on ${job.ship}`
                      ).join('\n')}`);
                    }
                  }}
                >
                  <div className="font-bold text-xs sm:text-sm mb-1">{date.getDate()}</div>
                  {dayJobs.length > 0 && (
                    <div className="text-[10px] sm:text-xs text-gray-600">
                      {dayJobs.length} job{dayJobs.length > 1 ? 's' : ''}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
        {/* Calendar View - Hidden on small screens */}
        <div className="hidden md:block mb-8">
          <h3 className="text-lg font-medium mb-2">Maintenance Calendar</h3>
          <div className="grid grid-cols-7 gap-1">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="text-center font-bold p-2 bg-gray-100">
                {day}
              </div>
            ))}
            {Array.from({ length: 35 }, (_, i) => {
              const date = new Date();
              date.setDate(i + 1);
              const dayJobs = jobs.filter(job => new Date(job.startDate).getDate() === date.getDate());
              
              return (
                <div
                  key={i}
                  className={`p-2 border border-gray-200 min-h-16 cursor-pointer transition-colors hover:bg-gray-50 ${dayJobs.length ? 'bg-gray-50' : 'bg-white'}`}
                  onClick={() => {
                    if (dayJobs.length) {
                      alert(`Jobs on ${date.toLocaleDateString()}:\n${dayJobs.map(job => 
                        `- ${job.type} of ${job.component} on ${job.ship}`
                      ).join('\n')}`);
                    }
                  }}
                >
                  <div className="font-bold mb-1">{date.getDate()}</div>
                  {dayJobs.length > 0 && (
                    <div className="text-xs text-gray-600">
                      {dayJobs.length} job{dayJobs.length > 1 ? 's' : ''}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
        
        {/* Jobs List - Card view on mobile, Table on larger screens */}
        <div className="overflow-x-auto">
          {/* Mobile Card View */}
          <div className="md:hidden">
            {filteredJobs.length > 0 ? (
              filteredJobs.map(job => (
                <div key={job.id} className="mb-4 p-4 border border-gray-200 rounded-lg shadow-sm">
                  <div className="flex justify-between mb-2">
                    <span className="font-bold">Job #{job.id}</span>
                    <span className={`font-bold ${
                      job.priority === 'High' ? 'text-red-500' : 
                      job.priority === 'Medium' ? 'text-yellow-500' : 'text-green-500'
                    }`}>
                      {job.priority} Priority
                    </span>
                  </div>
                  
                  <div className="grid grid-cols-2 gap-2 mb-3">
                    <div>
                      <div className="text-sm text-gray-600">Ship</div>
                      <div>{job.ship}</div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Component</div>
                      <div>{job.component}</div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Type</div>
                      <div>{job.type}</div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Status</div>
                      <div>{job.status}</div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Start Date</div>
                      <div>{job.startDate}</div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Assigned To</div>
                      <div>{job.assignedTo}</div>
                    </div>
                  </div>
                  
                  {job.status !== 'Completed' && (
                    <div className="flex gap-2 justify-end">
                      <button 
                        onClick={() => updateJobStatus(job.id, 'In Progress')}
                        className="px-3 py-1 bg-blue-500 text-white rounded text-sm disabled:bg-blue-300"
                        disabled={job.status === 'In Progress'}
                      >
                        Start
                      </button>
                      <button 
                        onClick={() => updateJobStatus(job.id, 'Completed')}
                        className="px-3 py-1 bg-green-500 text-white rounded text-sm"
                      >
                        Complete
                      </button>
                    </div>
                  )}
                </div>
              ))
            ) : (
              <div className="text-center py-6 text-gray-500">
                No jobs found matching your filters
              </div>
            )}
          </div>
          
          {/* Desktop Table View */}
          <table className="w-full hidden md:table">
            <thead>
              <tr className="bg-gray-100">
                <th className="p-3 text-left">Job ID</th>
                <th className="p-3 text-left">Ship</th>
                <th className="p-3 text-left">Component</th>
                <th className="p-3 text-left">Type</th>
                <th className="p-3 text-left">Priority</th>
                <th className="p-3 text-left">Status</th>
                <th className="p-3 text-left">Start Date</th>
                <th className="p-3 text-left">Assigned To</th>
                <th className="p-3 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredJobs.length > 0 ? (
                filteredJobs.map(job => (
                  <tr key={job.id} className="border-b border-gray-200">
                    <td className="p-3">#{job.id}</td>
                    <td className="p-3">{job.ship}</td>
                    <td className="p-3">{job.component}</td>
                    <td className="p-3">{job.type}</td>
                    <td className="p-3">
                      <span className={`font-bold ${
                        job.priority === 'High' ? 'text-red-500' : 
                        job.priority === 'Medium' ? 'text-yellow-500' : 'text-green-500'
                      }`}>
                        {job.priority}
                      </span>
                    </td>
                    <td className="p-3">{job.status}</td>
                    <td className="p-3">{job.startDate}</td>
                    <td className="p-3">{job.assignedTo}</td>
                    <td className="p-3">
                      {job.status !== 'Completed' && (
                        <div className="flex gap-2">
                          <button 
                            onClick={() => updateJobStatus(job.id, 'In Progress')}
                            className="px-3 py-1 bg-blue-500 text-white rounded text-sm disabled:bg-blue-300"
                            disabled={job.status === 'In Progress'}
                          >
                            Start
                          </button>
                          <button 
                            onClick={() => updateJobStatus(job.id, 'Completed')}
                            className="px-3 py-1 bg-green-500 text-white rounded text-sm"
                          >
                            Complete
                          </button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="9" className="p-6 text-center text-gray-500">
                    No jobs found matching your filters
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default MaintenanceJobs;
