import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const NAV_ITEMS = [
  { label: 'Dashboard', path: '/dashboard' },
  { label: 'Ship Management', path: '/ships' },
  { label: 'Jobs', path: '/jobs' },
  { label: 'Components', path: '/components/' },
];

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const menuRef = useRef();
  const hamburgerRef = useRef();

  const isActive = (path) => location.pathname.startsWith(path);

  const isMobile = () => window.innerWidth <= 1024;

  useEffect(() => {
    if (!menuOpen) return;
    function handleClick(e) {
      if (
        menuRef.current &&
        !menuRef.current.contains(e.target) &&
        hamburgerRef.current &&
        !hamburgerRef.current.contains(e.target)
      ) {
        setMenuOpen(false);
      }
    }
    function handleEsc(e) {
      if (e.key === 'Escape') setMenuOpen(false);
    }
    document.addEventListener('mousedown', handleClick);
    document.addEventListener('keydown', handleEsc);
    return () => {
      document.removeEventListener('mousedown', handleClick);
      document.removeEventListener('keydown', handleEsc);
    };
  }, [menuOpen]);

  useEffect(() => {
    if (!menuOpen) return;
    const firstBtn = menuRef.current?.querySelector('button, [tabindex="0"]');
    firstBtn && firstBtn.focus();
  }, [menuOpen]);

  const handleNavClick = (to) => {
    setMenuOpen(false);
    navigate(to);
  };

  const handleLogout = () => {
    setShowLogoutConfirm(true);
    setMenuOpen(false);
  };

  const confirmLogout = () => {
    logout();
    setShowLogoutConfirm(false);
    navigate('/signup');
  };

  const cancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  const styles = {
    nav: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '0.5rem 1rem',
      backgroundColor: '#2c3e50',
      color: '#ecf0f1',
      position: 'relative',
      flexWrap: 'wrap',
    },
    logo: {
      cursor: 'pointer',
      fontWeight: 700,
      fontSize: '1.3rem',
      color: '#fff',
    },
    linkList: {
      display: isMobile() ? 'none' : 'flex',
      listStyle: 'none',
      margin: 0,
      padding: 0,
      gap: '1rem',
      alignItems: 'center',
    },
    linkButton: {
      background: 'none',
      border: 'none',
      color: '#ecf0f1',
      fontSize: '1rem',
      cursor: 'pointer',
    },
    activeLink: {
      fontWeight: 'bold',
      textDecoration: 'underline',
    },
    toggleButton: {
      display: isMobile() ? 'flex' : 'none',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '30px',
      cursor: 'pointer',
      background: 'none',
      border: 'none',
    },
    toggleBar: {
      width: '100%',
      height: '3px',
      backgroundColor: '#ecf0f1',
      margin: '3px 0',
    },
    mobileMenu: {
      display: menuOpen ? 'block' : 'none',
      position: 'absolute',
      top: '100%',
      left: 0,
      width: '100%',
      backgroundColor: '#2c3e50',
      padding: '1rem 0',
      boxShadow: '0 8px 32px rgba(44,62,80,0.18)',
    },
    mobileList: {
      listStyle: 'none',
      padding: 0,
      margin: 0,
      display: 'flex',
      flexDirection: 'column',
      gap: '1rem',
    },
    logoutOverlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
    },
    logoutDialog: {
      backgroundColor: '#fff',
      padding: '2rem',
      borderRadius: '8px',
      textAlign: 'center',
    },
    logoutActions: {
      marginTop: '1rem',
      display: 'flex',
      justifyContent: 'center',
      gap: '1rem',
    },
  };

  return (
    <nav style={styles.nav} role="navigation" aria-label="Main Navigation">
      <span
        style={styles.logo}
        onClick={() => handleNavClick('/dashboard')}
        tabIndex={0}
        aria-label="ShipMaster Home"
        onKeyDown={e => (e.key === 'Enter' || e.key === ' ') && handleNavClick('/dashboard')}
      >
        ðŸš¢ ShipMaster
      </span>

      <ul style={styles.linkList} role="menubar">
        {user && NAV_ITEMS.map(item => (
          <li key={item.path}>
            <button
              onClick={() => handleNavClick(item.path)}
              style={{
                ...styles.linkButton,
                ...(isActive(item.path) ? styles.activeLink : {}),
              }}
              aria-current={isActive(item.path) ? 'page' : undefined}
              role="menuitem"
            >
              {item.label}
            </button>
          </li>
        ))}
        <li>
          <button
            onClick={toggleTheme}
            style={styles.linkButton}
            aria-label="Toggle dark mode"
            role="menuitem"
          >
            {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
          </button>
        </li>
        {user && (
          <li>
            <button
              onClick={handleLogout}
              style={styles.linkButton}
              aria-label="Logout"
              role="menuitem"
            >
              Logout
            </button>
          </li>
        )}
      </ul>

      <button
        onClick={() => setMenuOpen(open => !open)}
        aria-label={menuOpen ? 'Close navigation menu' : 'Open navigation menu'}
        aria-expanded={menuOpen}
        aria-controls="navbar-links-mobile"
        ref={hamburgerRef}
        style={styles.toggleButton}
      >
        <span style={styles.toggleBar} />
        <span style={styles.toggleBar} />
        <span style={styles.toggleBar} />
      </button>

      <div id="navbar-links-mobile" ref={menuRef} style={styles.mobileMenu}>
        <ul style={styles.mobileList} role="menu" aria-label="Mobile Navigation">
          {user && NAV_ITEMS.map(item => (
            <li key={item.path}>
              <button
                onClick={() => handleNavClick(item.path)}
                style={styles.linkButton}
                role="menuitem"
              >
                {item.label}
              </button>
            </li>
          ))}
          <li>
            <button
              onClick={() => { toggleTheme(); setMenuOpen(false); }}
              style={styles.linkButton}
              role="menuitem"
            >
              {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
            </button>
          </li>
          {user && (
            <li>
              <button
                onClick={handleLogout}
                style={styles.linkButton}
                role="menuitem"
              >
                Logout
              </button>
            </li>
          )}
        </ul>
      </div>

      {showLogoutConfirm && (
        <div style={styles.logoutOverlay} role="dialog" aria-modal="true" tabIndex={-1}>
          <div style={styles.logoutDialog}>
            <p>Are you sure you want to logout?</p>
            <div style={styles.logoutActions}>
              <button onClick={confirmLogout} style={styles.linkButton}>Yes, Logout</button>
              <button onClick={cancelLogout} style={styles.linkButton}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
