import React, { useState, useEffect } from 'react';
import { JobsStatusChart, MaintenanceStatusChart } from '../components/Dashboard/Charts';
import KPICards from '../components/Dashboard/KPICards';
import './DashboardPage.css';

const DashboardPage = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [filters, setFilters] = useState({
    timeRange: 'month',
    shipType: 'all',
    priority: 'all'
  });

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Fetch data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        await new Promise(resolve => setTimeout(resolve, 800));
        
        const mockData = {
          stats: {
            totalShips: 12,
            overdueMaintenance: 4,
            jobsInProgress: 6,
            jobsCompleted: 18,
            highPriorityJobs: 3
          },
          charts: {
            jobsStatus: { inProgress: 6, completed: 18 },
            maintenanceStatus: { overdue: 4, onSchedule: 8 }
          },
          recentActivities: [
            {
              id: 1,
              ship: "Ocean Voyager",
              component: "Engine",
              action: "Routine inspection",
              status: "in-progress",
              priority: "high",
              timestamp: new Date().toISOString()
            },
            // ... more activities
          ],
          ships: ["Ocean Voyager", "Pacific Star", "Atlantic Explorer"]
        };

        setDashboardData(mockData);
      } catch (err) {
        console.error("Fetch error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [filters]);

  if (loading) return <div className="loading-spinner">Loading...</div>;
  if (!dashboardData) return <div className="error-message">Data unavailable</div>;

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Ship Management</h1>
        {!isMobile && (
          <div className="filters-desktop">
            <FilterControls filters={filters} setFilters={setFilters} />
          </div>
        )}
      </header>

      {isMobile && (
        <div className="filters-mobile">
          <FilterControls filters={filters} setFilters={setFilters} isMobile />
        </div>
      )}

      <main>
        <KPICards data={dashboardData.stats} isMobile={isMobile} />
        
        <div className="charts-container">
          <ChartCard title="Jobs Status" isMobile={isMobile}>
            <JobsStatusChart data={dashboardData.charts.jobsStatus} />
          </ChartCard>
          <ChartCard title="Maintenance Status" isMobile={isMobile}>
            <MaintenanceStatusChart 
              data={dashboardData.charts.maintenanceStatus} 
              totalShips={dashboardData.stats.totalShips} 
            />
          </ChartCard>
        </div>

        <ActivitySection activities={dashboardData.recentActivities} />
      </main>
    </div>
  );
};

// Reusable Filter Component
const FilterControls = ({ filters, setFilters, isMobile }) => (
  <div className={`filter-controls ${isMobile ? 'mobile' : ''}`}>
    <select
      name="timeRange"
      value={filters.timeRange}
      onChange={(e) => setFilters({...filters, timeRange: e.target.value})}
      aria-label="Time range filter"
    >
      <option value="week">Last Week</option>
      <option value="month">Last Month</option>
      <option value="quarter">Last Quarter</option>
    </select>
    
    {/* Other filter controls... */}
  </div>
);

// Reusable Chart Card Component
const ChartCard = ({ title, children, isMobile }) => (
  <div className={`chart-card ${isMobile ? 'mobile' : ''}`}>
    <h3>{title}</h3>
    <div className="chart-wrapper">
      {children}
    </div>
  </div>
);

// Reusable Activity Component
const ActivitySection = ({ activities }) => (
  <section className="recent-activity">
    <h2>Recent Activity</h2>
    <div className="activity-list">
      {activities.map(activity => (
        <ActivityItem key={activity.id} activity={activity} />
      ))}
    </div>
  </section>
);

export default DashboardPage;
