import React, { useState, useRef, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const NAV_LINKS = [
  { label: 'Home', to: '/dashboard' },
  { label: 'Ships List', to: '/ships' },
  { label: 'Add Ship', to: '/ships/add' },
  { label: 'Maintenance', to: '/maintenance' },
  { label: 'Account', to: '/account' },
];

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef();
  const hamburgerRef = useRef();

  // Close menu on outside click or Escape
  useEffect(() => {
    if (!menuOpen) return;
    function handleClick(e) {
      if (
        menuRef.current &&
        !menuRef.current.contains(e.target) &&
        hamburgerRef.current &&
        !hamburgerRef.current.contains(e.target)
      ) {
        setMenuOpen(false);
      }
    }
    function handleEsc(e) {
      if (e.key === 'Escape') setMenuOpen(false);
    }
    document.addEventListener('mousedown', handleClick);
    document.addEventListener('keydown', handleEsc);
    return () => {
      document.removeEventListener('mousedown', handleClick);
      document.removeEventListener('keydown', handleEsc);
    };
  }, [menuOpen]);

  // Trap focus in menu when open (basic)
  useEffect(() => {
    if (!menuOpen) return;
    const firstBtn = menuRef.current?.querySelector('button, a');
    firstBtn && firstBtn.focus();
  }, [menuOpen]);

  const handleLogout = () => {
    logout();
    setMenuOpen(false);
    navigate('/signup');
  };

  const handleNavClick = (to) => {
    setMenuOpen(false);
    navigate(to);
  };

  const isActive = (to) => location.pathname === to;

  return (
    <nav className="navbar navbar-expand-lg" role="navigation" aria-label="Main Navigation">
      <span
        className="navbar-logo"
        onClick={() => handleNavClick('/dashboard')}
        style={{ cursor: 'pointer', fontWeight: 700, fontSize: '1.3rem', color: '#fff' }}
        tabIndex={0}
        aria-label="ShipMaster Home"
        onKeyDown={e => (e.key === 'Enter' || e.key === ' ') && handleNavClick('/dashboard')}
      >
        🚢 ShipMaster
      </span>
      {/* Desktop nav links */}
      <ul className="navbar-links navbar-expand-lg">
        {NAV_LINKS.map(link => (
          <li key={link.to}>
            <button
              onClick={() => handleNavClick(link.to)}
              className={`nav-link${isActive(link.to) ? ' active' : ''}`}
              aria-current={isActive(link.to) ? 'page' : undefined}
            >
              {link.label}
            </button>
          </li>
        ))}
        <li>
          <button onClick={toggleTheme} className="nav-btn">
            {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
          </button>
        </li>
        {user && (
          <li>
            <button onClick={handleLogout} className="nav-btn">Logout</button>
          </li>
        )}
      </ul>
      {/* Hamburger for mobile/tablet */}
      <button
        className="navbar-toggle"
        aria-label={menuOpen ? 'Close navigation menu' : 'Open navigation menu'}
        aria-expanded={menuOpen}
        aria-controls="mobile-menu"
        onClick={() => setMenuOpen((open) => !open)}
        ref={hamburgerRef}
        tabIndex={0}
      >
        <span className="navbar-toggle-bar" />
        <span className="navbar-toggle-bar" />
        <span className="navbar-toggle-bar" />
      </button>
      {/* Mobile/tablet dropdown menu */}
      <ul
        id="mobile-menu"
        className={`navbar-links-mobile mobile-menu${menuOpen ? ' open' : ''}`}
        ref={menuRef}
        role="menu"
        aria-label="Mobile Navigation"
      >
        {NAV_LINKS.map(link => (
          <li key={link.to}>
            <button
              onClick={() => handleNavClick(link.to)}
              className={`nav-link${isActive(link.to) ? ' active' : ''}`}
              role="menuitem"
              tabIndex={menuOpen ? 0 : -1}
              aria-current={isActive(link.to) ? 'page' : undefined}
            >
              {link.label}
            </button>
          </li>
        ))}
        <li>
          <button onClick={() => { toggleTheme(); setMenuOpen(false); }} className="nav-btn" role="menuitem" tabIndex={menuOpen ? 0 : -1}>
            {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
          </button>
        </li>
        {user && (
          <li>
            <button onClick={handleLogout} className="nav-btn" role="menuitem" tabIndex={menuOpen ? 0 : -1}>Logout</button>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
