import React from 'react';
import KPICards from '../components/Dashboard/KPICards';
import Charts from '../components/Dashboard/Charts';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';

const DashboardPage = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const { theme, toggleTheme } = useTheme();

  const handleLogout = () => {
    logout();
    navigate('/signup');
  };

  return (
    <div className="dashboard-page">
      <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', gap: '0.5rem', marginBottom: '1rem' }}>
        <button onClick={toggleTheme} style={{ minWidth: 90 }}>
          {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
        </button>
        <button onClick={handleLogout} style={{ minWidth: 90 }}>
          Logout
        </button>
      </div>
      <h1>Dashboard</h1>
      {user && <p style={{marginBottom: '1rem'}}>Logged in as <b>{user.email}</b> (<b>{user.role}</b>)</p>}
      <KPICards />
      <Charts />
    </div>
  );
};

export default DashboardPage;
