import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useShips } from '../../contexts/ShipsContext';

const ShipList = ({ onEdit }) => {
  const navigate = useNavigate();
  const { ships, deleteShip } = useShips();

  const handleShipClick = (shipId) => {
    navigate(`/ships/${shipId}`);
  };

  return (
    <div className="ship-list">
      <h2>Ships</h2>
      <table className="ship-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>IMO Number</th>
            <th>Flag</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {ships.map((ship) => (
            <tr key={ship.id}>
              <td style={{ cursor: 'pointer', color: '#1976d2' }} onClick={() => handleShipClick(ship.id)}>{ship.name}</td>
              <td>{ship.imoNumber}</td>
              <td>{ship.flag}</td>
              <td>{ship.status}</td>
              <td>
                <button onClick={() => onEdit(ship)}>Edit</button>
                <button onClick={() => deleteShip(ship.id)} style={{ marginLeft: 8, color: 'red' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ShipList;
