import React, { useState, useEffect } from 'react';
import { JobsStatusChart, MaintenanceStatusChart } from '../components/Dashboard/Charts';
import KPICards from '../components/Dashboard/KPICards';
import './DashboardPage.css';

const DashboardPage = () => {
  // State management
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    timeRange: 'month',
    shipType: 'all',
    priority: 'all'
  });

  // Simulate API data fetching
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 800));
        
        // Mock data response
        const mockData = {
          stats: {
            totalShips: 12,
            overdueMaintenance: 4,
            jobsInProgress: 6,
            jobsCompleted: 18,
            highPriorityJobs: 3
          },
          charts: {
            jobsStatus: {
              inProgress: 6,
              completed: 18
            },
            maintenanceStatus: {
              overdue: 4,
              onSchedule: 8
            }
          },
          recentActivities: [
            {
              id: 1,
              ship: "Ocean Voyager",
              component: "Engine",
              action: "Routine inspection",
              status: "in-progress",
              priority: "high",
              timestamp: "2025-05-12T10:30:00"
            },
            {
              id: 2,
              ship: "Pacific Star",
              component: "Navigation",
              action: "GPS calibration",
              status: "completed",
              priority: "medium",
              timestamp: "2025-05-11T14:15:00"
            }
          ],
          ships: ["Ocean Voyager", "Pacific Star", "Atlantic Explorer"]
        };

        // Apply filters to mock data
        if (filters.timeRange === 'week') {
          mockData.stats.jobsInProgress = 3;
          mockData.stats.jobsCompleted = 7;
          mockData.charts.jobsStatus.inProgress = 3;
          mockData.charts.jobsStatus.completed = 7;
        }

        if (filters.priority === 'high') {
          mockData.recentActivities = mockData.recentActivities.filter(
            activity => activity.priority === 'high'
          );
        }

        setDashboardData(mockData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [filters]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const formatTimeAgo = (timestamp) => {
    const now = new Date();
    const date = new Date(timestamp);
    const diffHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffHours < 24) return `${diffHours} hours ago`;
    return `${Math.floor(diffHours / 24)} days ago`;
  };

  if (loading) return <div className="loading-spinner">Loading Dashboard...</div>;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Ship Management Dashboard</h1>
        <div className="dashboard-filters">
          <div className="filter-group">
            <label>Time Range:</label>
            <select
              name="timeRange"
              value={filters.timeRange}
              onChange={handleFilterChange}
            >
              <option value="week">Last Week</option>
              <option value="month">Last Month</option>
              <option value="quarter">Last Quarter</option>
            </select>
          </div>
          
          <div className="filter-group">
            <label>Ship:</label>
            <select
              name="shipType"
              value={filters.shipType}
              onChange={handleFilterChange}
            >
              <option value="all">All Ships</option>
              {dashboardData?.ships.map(ship => (
                <option key={ship} value={ship.toLowerCase().replace(' ', '-')}>
                  {ship}
                </option>
              ))}
            </select>
          </div>
          
          <div className="filter-group">
            <label>Priority:</label>
            <select
              name="priority"
              value={filters.priority}
              onChange={handleFilterChange}
            >
              <option value="all">All Priorities</option>
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </select>
          </div>
        </div>
      </div>

      <KPICards 
        totalShips={dashboardData.stats.totalShips}
        overdueComponents={dashboardData.stats.overdueMaintenance}
        jobsInProgress={dashboardData.stats.jobsInProgress}
        jobsCompleted={dashboardData.stats.jobsCompleted}
      />

      <div className="charts-container">
        <div className="chart-card">
          <h3>Jobs Status</h3>
          <JobsStatusChart 
            inProgress={dashboardData.charts.jobsStatus.inProgress}
            completed={dashboardData.charts.jobsStatus.completed}
          />
        </div>
        
        <div className="chart-card">
          <h3>Maintenance Status</h3>
          <MaintenanceStatusChart 
            overdue={dashboardData.charts.maintenanceStatus.overdue}
            totalShips={dashboardData.stats.totalShips}
          />
        </div>
      </div>

      <div className="recent-activity">
        <div className="section-header">
          <h2>Recent Activity</h2>
          <button 
            className="refresh-btn"
            onClick={() => window.location.reload()}
          >
            ↻ Refresh
          </button>
        </div>
        
        <div className="activity-list">
          {dashboardData.recentActivities.map(activity => (
            <div 
              key={activity.id} 
              className={`activity-item ${activity.status} priority-${activity.priority}`}
            >
              <div className="activity-icon">
                {activity.status === 'completed' ? '✓' : '↻'}
              </div>
              
              <div className="activity-content">
                <div className="activity-header">
                  <span className="ship-name">{activity.ship}</span>
                  <span className={`priority-badge ${activity.priority}`}>
                    {activity.priority}
                  </span>
                </div>
                <p className="activity-desc">
                  {activity.component} - {activity.action}
                </p>
                <div className="activity-footer">
                  <span className="timestamp">
                    {formatTimeAgo(activity.timestamp)}
                  </span>
                  <button className="action-btn">
                    {activity.status === 'completed' ? 'View' : 'Update'}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
