import React from 'react';
import { JobsStatusChart, MaintenanceStatusChart } from '../components/Dashboard/Charts';
import KPICards from '../components/Dashboard/KPICards';
import './DashboardPage.css';

const DashboardPage = () => {
  // Data based on your screenshots
  const dashboardData = {
    ships: {
      total: 10,
      overdueMaintenance: 3,
    },
    jobs: {
      inProgress: 5,
      completed: 12,
      highPriority: 2,
    },
    components: {
      maintenanceNeeded: 3,
    },
    recentActivities: [
      {
        id: 1,
        ship: "Ocean Voyager",
        action: "Maintenance started",
        time: "2 hours ago",
        status: "in-progress"
      },
      {
        id: 2,
        ship: "Pacific Star",
        action: "Inspection completed",
        time: "1 day ago",
        status: "completed"
      }
    ]
  };

  return (
    <div className="dashboard-container">
      <h1>Ship Management Dashboard</h1>
      
      {/* KPI Cards Row */}
      <div className="kpi-row">
        <KPICards data={{
          totalShips: dashboardData.ships.total,
          overdueComponents: dashboardData.components.maintenanceNeeded,
          jobsInProgress: dashboardData.jobs.inProgress,
          jobsCompleted: dashboardData.jobs.completed
        }} />
      </div>
      
      {/* Charts Row */}
      <div className="charts-row">
        <div className="chart-container">
          <JobsStatusChart 
            inProgress={dashboardData.jobs.inProgress} 
            completed={dashboardData.jobs.completed} 
          />
        </div>
        
        <div className="chart-container">
          <MaintenanceStatusChart 
            overdue={dashboardData.ships.overdueMaintenance}
            totalShips={dashboardData.ships.total}
          />
        </div>
      </div>
      
      {/* Recent Activity Section */}
      <div className="recent-activity">
        <h2>Recent Activity</h2>
        <div className="activity-list">
          {dashboardData.recentActivities.map(activity => (
            <div key={activity.id} className={`activity-item ${activity.status}`}>
              <div className="activity-icon">
                {activity.status === 'completed' ? '✓' : '↻'}
              </div>
              <div className="activity-details">
                <p className="activity-ship">{activity.ship}</p>
                <p className="activity-action">{activity.action}</p>
              </div>
              <p className="activity-time">{activity.time}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
