import React from 'react';

const KPICards = ({ data }) => {
  const cardContainerStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '20px',
    marginBottom: '30px'
  };

  const cardStyle = {
    background: 'white',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)',
    padding: '20px',
    display: 'flex',
    alignItems: 'center',
    transition: 'transform 0.2s, box-shadow 0.2s',
    ':hover': {
      transform: 'translateY(-5px)',
      boxShadow: '0 6px 12px rgba(0, 0, 0, 0.1)'
    }
  };

  const iconStyle = (color) => ({
    width: '60px',
    height: '60px',
    borderRadius: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: '20px',
    backgroundColor: `${color}20`,
    fontSize: '24px',
    color: color
  });

  const cardTitleStyle = {
    margin: '0 0 5px 0',
    fontSize: '16px',
    color: '#7f8c8d'
  };

  const cardValueStyle = {
    margin: '0',
    fontSize: '28px',
    fontWeight: 'bold',
    color: '#2c3e50'
  };

  const cardDescStyle = {
    margin: '5px 0 0 0',
    fontSize: '14px',
    color: '#95a5a6'
  };

  return (
    <div style={cardContainerStyle}>
      {/* Total Ships Card */}
      <div style={cardStyle}>
        <div style={iconStyle('#3498db')}>üö¢</div>
        <div>
          <h3 style={cardTitleStyle}>Total Ships</h3>
          <p style={cardValueStyle}>{data.totalShips}</p>
          <p style={cardDescStyle}>Active vessels in fleet</p>
        </div>
      </div>

      {/* Overdue Components Card */}
      <div style={cardStyle}>
        <div style={iconStyle('#e74c3c')}>‚ö†Ô∏è</div>
        <div>
          <h3 style={cardTitleStyle}>Overdue Maintenance</h3>
          <p style={cardValueStyle}>{data.overdueComponents}</p>
          <p style={cardDescStyle}>Components needing attention</p>
        </div>
      </div>

      {/* Jobs in Progress Card */}
      <div style={cardStyle}>
        <div style={iconStyle('#f39c12')}>üìã</div>
        <div>
          <h3 style={cardTitleStyle}>Jobs In Progress</h3>
          <p style={cardValueStyle}>{data.jobsInProgress}</p>
          <p style={cardDescStyle}>Active maintenance jobs</p>
        </div>
      </div>

      {/* Completed Jobs Card */}
      <div style={cardStyle}>
        <div style={iconStyle('#2ecc70')}>‚úÖ</div>
        <div>
          <h3 style={cardTitleStyle}>Completed Jobs</h3>
          <p style={cardValueStyle}>{data.jobsCompleted}</p>
          <p style={cardDescStyle}>Jobs finished this period</p>
        </div>
      </div>
    </div>
  );
};

export default KPICards;
