import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/signup');
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <span className="navbar-logo" onClick={() => navigate('/dashboard')} style={{ cursor: 'pointer', fontWeight: 700, fontSize: '1.3rem', color: '#1976d2' }}>
          ðŸš¢ ShipMaster
        </span>
        {user && (
          <>
            <NavLink to="/dashboard" className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}>Dashboard</NavLink>
            <NavLink to="/ships" className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}>Ships</NavLink>
            <NavLink to="/jobs" className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}>Jobs</NavLink>
          </>
        )}
      </div>
      <div className="navbar-right">
        <button onClick={toggleTheme} className="nav-btn">
          {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
        </button>
        {user && (
          <button onClick={handleLogout} className="nav-btn">Logout</button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
