import React, { useState, useEffect } from 'react';

const MaintenanceJobs = () => {
  // Sample engineers data
  const engineers = [
    { id: 1, name: 'John Smith' },
    { id: 2, name: 'Sarah Johnson' },
    { id: 3, name: 'Michael Brown' }
  ];

  // Sample ships data
  const ships = [
    { id: 1, name: 'Ocean Voyager' },
    { id: 2, name: 'Marine Express' },
    { id: 3, name: 'Pacific Carrier' }
  ];

  // Sample components data
  const components = [
    { id: 1, name: 'Engine' },
    { id: 2, name: 'Propeller' },
    { id: 3, name: 'Navigation System' },
    { id: 4, name: 'Cooling System' }
  ];

  // Job types
  const jobTypes = ['Inspection', 'Repair', 'Maintenance', 'Replacement'];

  // State for jobs and form
  const [jobs, setJobs] = useState([
    {
      id: 1,
      ship: 'Ocean Voyager',
      component: 'Engine',
      type: 'Inspection',
      priority: 'High',
      status: 'In Progress',
      startDate: 'May 12, 2025',
      assignedTo: 'John Smith'
    },
    {
      id: 2,
      ship: 'Marine Express',
      component: 'Propeller',
      type: 'Repair',
      priority: 'Medium',
      status: 'Pending',
      startDate: 'May 14, 2025',
      assignedTo: 'Sarah Johnson'
    },
    {
      id: 3,
      ship: 'Pacific Carrier',
      component: 'Navigation System',
      type: 'Maintenance',
      priority: 'Low',
      status: 'Pending',
      startDate: 'May 15, 2025',
      assignedTo: 'Michael Brown'
    }
  ]);

  const [formData, setFormData] = useState({
    ship: '',
    component: '',
    type: '',
    priority: 'Medium',
    assignedTo: ''
  });

  // Filter states
  const [filterShip, setFilterShip] = useState('All Ships');
  const [filterStatus, setFilterStatus] = useState('All Statuses');
  const [filterPriority, setFilterPriority] = useState('All Priorities');

  // Calendar states
  const [currentDate, setCurrentDate] = useState(new Date());
  const [calendarView, setCalendarView] = useState('month'); // 'month' or 'week'
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedDayJobs, setSelectedDayJobs] = useState([]);
  const [showJobsModal, setShowJobsModal] = useState(false);

  // Create new job
  const createJob = (e) => {
    e.preventDefault();
    const newJob = {
      id: jobs.length + 1,
      ship: formData.ship,
      component: formData.component,
      type: formData.type,
      priority: formData.priority,
      status: 'Pending',
      startDate: new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' }),
      assignedTo: formData.assignedTo
    };
    setJobs([...jobs, newJob]);
    setFormData({
      ship: '',
      component: '',
      type: '',
      priority: 'Medium',
      assignedTo: ''
    });
  };

  // Update job status
  const updateJobStatus = (jobId, newStatus) => {
    setJobs(jobs.map(job => 
      job.id === jobId ? { ...job, status: newStatus } : job
    ));
  };

  // Filter jobs
  const filteredJobs = jobs.filter(job => {
    return (
      (filterShip === 'All Ships' || job.ship === filterShip) &&
      (filterStatus === 'All Statuses' || job.status === filterStatus) &&
      (filterPriority === 'All Priorities' || job.priority === filterPriority)
    );
  });

  // Calendar utility functions
  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year, month) => {
    return new Date(year, month, 1).getDay();
  };

  const getWeekDays = (date) => {
    const day = date.getDay();
    const result = [];
    
    // Calculate the first day of the week (Sunday)
    const first = new Date(date);
    first.setDate(date.getDate() - day);
    
    // Generate array of dates for the week
    for (let i = 0; i < 7; i++) {
      const currentDate = new Date(first);
      currentDate.setDate(first.getDate() + i);
      result.push(currentDate);
    }
    
    return result;
  };

  const handlePrevMonth = () => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() - 1);
    setCurrentDate(newDate);
  };

  const handleNextMonth = () => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + 1);
    setCurrentDate(newDate);
  };

  const handlePrevWeek = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(currentDate.getDate() - 7);
    setCurrentDate(newDate);
  };

  const handleNextWeek = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(currentDate.getDate() + 7);
    setCurrentDate(newDate);
  };

  const handleDateClick = (date) => {
    const formattedDate = date.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
    const dayJobs = jobs.filter(job => {
      const jobDate = new Date(job.startDate);
      return jobDate.getDate() === date.getDate() && 
             jobDate.getMonth() === date.getMonth() && 
             jobDate.getFullYear() === date.getFullYear();
    });
    
    setSelectedDate(formattedDate);
    setSelectedDayJobs(dayJobs);
    setShowJobsModal(true);
  };

  // Generate calendar cells
  const renderCalendarMonth = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const daysInMonth = getDaysInMonth(year, month);
    const firstDay = getFirstDayOfMonth(year, month);
    
    const days = [];
    
    // Empty cells for days before the first day of month
    for (let i = 0; i < firstDay; i++) {
      days.push(
        <div key={`empty-${i}`} className="bg-gray-100 opacity-50" style={styles.calendarDay}></div>
      );
    }
    
    // Days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const formattedDate = date.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
      
      const dayJobs = jobs.filter(job => job.startDate === formattedDate);
      const hasJobs = dayJobs.length > 0;
      const isToday = new Date().toDateString() === date.toDateString();
      
      days.push(
        <div
          key={day}
          onClick={() => handleDateClick(date)}
          className={`${hasJobs ? 'bg-blue-50' : 'bg-white'} ${isToday ? 'border-blue-500 border-2' : ''}`}
          style={{
            ...styles.calendarDay,
            cursor: 'pointer',
            position: 'relative'
          }}
        >
          <div style={styles.calendarDate}>
            {day}
          </div>
          {hasJobs && (
            <div 
              className="text-blue-700 font-medium bg-blue-100 px-2 py-1 rounded-full text-xs"
              style={styles.calendarJobs}>
              {dayJobs.length} job{dayJobs.length > 1 ? 's' : ''}
            </div>
          )}
        </div>
      );
    }
    
    return days;
  };

  const renderCalendarWeek = () => {
    const weekDays = getWeekDays(currentDate);
    
    return weekDays.map((date, index) => {
      const formattedDate = date.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
      const dayJobs = jobs.filter(job => job.startDate === formattedDate);
      const hasJobs = dayJobs.length > 0;
      const isToday = new Date().toDateString() === date.toDateString();
      
      return (
        <div
          key={index}
          onClick={() => handleDateClick(date)}
          className={`${hasJobs ? 'bg-blue-50' : 'bg-white'} ${isToday ? 'border-blue-500 border-2' : ''}`}
          style={{
            ...styles.weekDay,
            cursor: 'pointer'
          }}
        >
          <div style={styles.weekDayHeader}>
            {date.toLocaleDateString('en-US', { weekday: 'short' })} {date.getDate()}
          </div>
          <div style={styles.weekDayContent}>
            {dayJobs.map(job => (
              <div 
                key={job.id} 
                className="bg-blue-100 p-2 mb-1 rounded text-xs"
                style={styles.weekDayJob}
              >
                <div className="font-bold">{job.type}</div>
                <div>{job.component} - {job.ship}</div>
              </div>
            ))}
          </div>
        </div>
      );
    });
  };

  // Close modal when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showJobsModal && e.target.classList.contains('modal-backdrop')) {
        setShowJobsModal(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showJobsModal]);

  return (
    <div className="p-4 sm:p-6 max-w-6xl mx-auto" style={styles.container}>
      <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center sm:text-left" style={styles.header}>Maintenance Jobs Management</h1>
      
      {/* Create Job Form */}
      <div className="bg-white rounded-lg shadow p-4 sm:p-6 mb-6" style={styles.card}>
        <h2 className="text-xl font-semibold mb-4">Create New Maintenance Job</h2>
        <form onSubmit={createJob}>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4" style={styles.formGroup}>
            <div style={styles.formItem}>
              <label style={styles.label}>Ship:</label>
              <select
                value={formData.ship}
                onChange={(e) => setFormData({...formData, ship: e.target.value})}
                required
                className="w-full p-2 border rounded"
                style={styles.select}
              >
                <option value="">Select Ship</option>
                {ships.map(ship => (
                  <option key={ship.id} value={ship.name}>{ship.name}</option>
                ))}
              </select>
            </div>

            <div style={styles.formItem}>
              <label style={styles.label}>Component:</label>
              <select
                value={formData.component}
                onChange={(e) => setFormData({...formData, component: e.target.value})}
                required
                className="w-full p-2 border rounded"
                style={styles.select}
              >
                <option value="">Select Component</option>
                {components.map(component => (
                  <option key={component.id} value={component.name}>{component.name}</option>
                ))}
              </select>
            </div>

            <div style={styles.formItem}>
              <label style={styles.label}>Job Type:</label>
              <select
                value={formData.type}
                onChange={(e) => setFormData({...formData, type: e.target.value})}
                required
                className="w-full p-2 border rounded"
                style={styles.select}
              >
                <option value="">Select Type</option>
                {jobTypes.map((type, index) => (
                  <option key={index} value={type}>{type}</option>
                ))}
              </select>
            </div>

            <div style={styles.formItem}>
              <label style={styles.label}>Priority:</label>
              <select
                value={formData.priority}
                onChange={(e) => setFormData({...formData, priority: e.target.value})}
                className="w-full p-2 border rounded"
                style={styles.select}
              >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </select>
            </div>

            <div style={styles.formItem}>
              <label style={styles.label}>Assigned Engineer:</label>
              <select
                value={formData.assignedTo}
                onChange={(e) => setFormData({...formData, assignedTo: e.target.value})}
                required
                className="w-full p-2 border rounded"
                style={styles.select}
              >
                <option value="">Select Engineer</option>
                {engineers.map(engineer => (
                  <option key={engineer.id} value={engineer.name}>{engineer.name}</option>
                ))}
              </select>
            </div>
          </div>
          <button 
            type="submit"
            className="mt-4 bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded"
            style={styles.submitButton}
          >
            Create Job
          </button>
        </form>
      </div>

      {/* Jobs List with Filters */}
      <div>
        <h2 className="text-xl font-semibold mb-4" style={styles.header}>Maintenance Jobs</h2>
        <div className="flex flex-wrap gap-4 mb-6" style={styles.filterGroup}>
          <div className="min-w-[200px] flex-1" style={styles.filterItem}>
            <label style={styles.label}>Filter by Ship:</label>
            <select 
              value={filterShip} 
              onChange={(e) => setFilterShip(e.target.value)}
              className="w-full p-2 border rounded"
              style={styles.select}
            >
              <option value="All Ships">All Ships</option>
              {ships.map(ship => (
                <option key={ship.id} value={ship.name}>{ship.name}</option>
              ))}
            </select>
          </div>

          <div className="min-w-[200px] flex-1" style={styles.filterItem}>
            <label style={styles.label}>Filter by Status:</label>
            <select 
              value={filterStatus} 
              onChange={(e) => setFilterStatus(e.target.value)}
              className="w-full p-2 border rounded"
              style={styles.select}
            >
              <option value="All Statuses">All Statuses</option>
              <option value="Pending">Pending</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>

          <div className="min-w-[200px] flex-1" style={styles.filterItem}>
            <label style={styles.label}>Filter by Priority:</label>
            <select 
              value={filterPriority} 
              onChange={(e) => setFilterPriority(e.target.value)}
              className="w-full p-2 border rounded"
              style={styles.select}
            >
              <option value="All Priorities">All Priorities</option>
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
          </div>
        </div>
        
        {/* Enhanced Calendar View - Responsive for all screen sizes */}
        <div className="bg-white rounded-lg shadow p-4 mb-6" style={styles.calendarContainer}>
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-semibold">Maintenance Calendar</h3>
            <div className="flex items-center space-x-4">
              <div className="flex border rounded overflow-hidden">
                <button 
                  onClick={() => setCalendarView('month')}
                  className={`px-3 py-1 ${calendarView === 'month' ? 'bg-blue-500 text-white' : 'bg-gray-100'}`}
                >
                  Month
                </button>
                <button 
                  onClick={() => setCalendarView('week')}
                  className={`px-3 py-1 ${calendarView === 'week' ? 'bg-blue-500 text-white' : 'bg-gray-100'}`}
                >
                  Week
                </button>
              </div>
            </div>
          </div>

          <div className="flex justify-between items-center mb-4">
            <h4 className="font-medium">
              {calendarView === 'month' 
                ? currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })
                : `Week of ${getWeekDays(currentDate)[0].toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} - ${getWeekDays(currentDate)[6].toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`
              }
            </h4>
            <div className="flex space-x-2">
              <button 
                onClick={calendarView === 'month' ? handlePrevMonth : handlePrevWeek}
                className="p-1 bg-gray-100 hover:bg-gray-200 rounded"
              >
                &lt;
              </button>
              <button
                onClick={() => setCurrentDate(new Date())}
                className="px-2 py-1 bg-gray-100 hover:bg-gray-200 rounded text-sm"
              >
                Today
              </button>
              <button 
                onClick={calendarView === 'month' ? handleNextMonth : handleNextWeek}
                className="p-1 bg-gray-100 hover:bg-gray-200 rounded"
              >
                &gt;
              </button>
            </div>
          </div>

          {/* Calendar Grid */}
          <div className="mb-4">
            {/* Day headers */}
            <div className="grid grid-cols-7 gap-1 mb-1">
              {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                <div key={day} className="text-center font-medium bg-gray-100 p-2" style={styles.calendarHeader}>
                  {day}
                </div>
              ))}
            </div>
            
            {/* Calendar body */}
            <div className={calendarView === 'month' ? "grid grid-cols-7 gap-1" : "grid grid-cols-7 gap-1"} style={calendarView === 'week' ? styles.weekCalendar : styles.calendar}>
              {calendarView === 'month' ? renderCalendarMonth() : renderCalendarWeek()}
            </div>
          </div>
        </div>
        
        {/* Jobs Table */}
        <div className="overflow-x-auto" style={styles.tableContainer}>
          <table className="w-full" style={styles.table}>
            <thead>
              <tr className="bg-gray-100" style={styles.tableHeader}>
                <th className="p-3 text-left" style={styles.tableCell}>Job ID</th>
                <th className="p-3 text-left" style={styles.tableCell}>Ship</th>
                <th className="p-3 text-left" style={styles.tableCell}>Component</th>
                <th className="p-3 text-left" style={styles.tableCell}>Type</th>
                <th className="p-3 text-left" style={styles.tableCell}>Priority</th>
                <th className="p-3 text-left" style={styles.tableCell}>Status</th>
                <th className="p-3 text-left" style={styles.tableCell}>Start Date</th>
                <th className="p-3 text-left" style={styles.tableCell}>Assigned To</th>
                <th className="p-3 text-left" style={styles.tableCell}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredJobs.length > 0 ? (
                filteredJobs.map(job => (
                  <tr key={job.id} className="border-b" style={styles.tableRow}>
                    <td className="p-3" style={styles.tableCell} data-label="Job ID">#{job.id}</td>
                    <td className="p-3" style={styles.tableCell} data-label="Ship">{job.ship}</td>
                    <td className="p-3" style={styles.tableCell} data-label="Component">{job.component}</td>
                    <td className="p-3" style={styles.tableCell} data-label="Type">{job.type}</td>
                    <td className="p-3" style={{ 
                      ...styles.tableCell,
                      color: job.priority === 'High' ? '#e74c3c' : 
                            job.priority === 'Medium' ? '#f39c12' : '#2ecc71',
                      fontWeight: 'bold'
                    }} data-label="Priority">
                      {job.priority}
                    </td>
                    <td className="p-3" style={styles.tableCell} data-label="Status">{job.status}</td>
                    <td className="p-3" style={styles.tableCell} data-label="Start Date">{job.startDate}</td>
                    <td className="p-3" style={styles.tableCell} data-label="Assigned To">{job.assignedTo}</td>
                    <td className="p-3" style={styles.tableCell} data-label="Actions">
                      {job.status !== 'Completed' && (
                        <div className="flex gap-2" style={styles.actionButtons}>
                          <button 
                            onClick={() => updateJobStatus(job.id, 'In Progress')}
                            className="bg-blue-500 text-white px-3 py-1 rounded text-sm"
                            style={styles.actionButton}
                            disabled={job.status === 'In Progress'}
                          >
                            Start
                          </button>
                          <button 
                            onClick={() => updateJobStatus(job.id, 'Completed')}
                            className="bg-green-500 text-white px-3 py-1 rounded text-sm"
                            style={{...styles.actionButton, backgroundColor: '#2ecc71'}}
                          >
                            Complete
                          </button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="9" className="p-4 text-center text-gray-500" style={styles.noJobs}>
                    No jobs found matching your filters
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal for displaying jobs on a specific date */}
      {showJobsModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 modal-backdrop">
          <div className="bg-white rounded-lg shadow-lg w-full max-w-md mx-4">
            <div className="p-4 border-b">
              <h3 className="text-lg font-semibold">Jobs for {selectedDate}</h3>
            </div>
            <div className="p-4 max-h-96 overflow-y-auto">
              {selectedDayJobs.length > 0 ? (
                selectedDayJobs.map(job => (
                  <div key={job.id} className="mb-4 p-3 border rounded">
                    <div className="flex justify-between">
                      <span className="font-semibold">Job #{job.id}</span>
                      <span className={`px-2 py-1 rounded text-xs ${
                        job.priority === 'High' ? 'bg-red-100 text-red-700' : 
                        job.priority === 'Medium' ? 'bg-yellow-100 text-yellow-700' : 
                        'bg-green-100 text-green-700'
                      }`}>
                        {job.priority}
                      </span>
                    </div>
                    <div className="mt-2 grid grid-cols-2 gap-2">
                      <div>
                        <div className="text-gray-500 text-sm">Ship</div>
                        <div>{job.ship}</div>
                      </div>
                      <div>
                        <div className="text-gray-500 text-sm">Component</div>
                        <div>{job.component}</div>
                      </div>
                      <div>
                        <div className="text-gray-500 text-sm">Type</div>
                        <div>{job.type}</div>
                      </div>
                      <div>
                        <div className="text-gray-500 text-sm">Status</div>
                        <div>{job.status}</div>
                      </div>
                      <div>
                        <div className="text-gray-500 text-sm">Assigned To</div>
                        <div>{job.assignedTo}</div>
                      </div>
                    </div>
                    {job.status !== 'Completed' && (
                      <div className="mt-3 flex gap-2">
                        <button 
                          onClick={() => {
                            updateJobStatus(job.id, 'In Progress');
                            setSelectedDayJobs(selectedDayJobs.map(j => 
                              j.id === job.id ? { ...j, status: 'In Progress' } : j
                            ));
                          }}
                          className="bg-blue-500 text-white px-3 py-1 rounded text-sm"
                          disabled={job.status === 'In Progress'}
                        >
                          Start
                        </button>
                        <button 
                          onClick={() => {
                            updateJobStatus(job.id, 'Completed');
                            setSelectedDayJobs(selectedDayJobs.map(j => 
                              j.id === job.id ? { ...j, status: 'Completed' } : j
                            ));
                          }}
                          className="bg-green-500 text-white px-3 py-1 rounded text-sm"
                        >
                          Complete
                        </button>
                      </div>
                    )}
                  </div>
                ))
              ) : (
                <div className="text-center py-6 text-gray-500">
                  No jobs scheduled for this date
                </div>
              )}
            </div>
            <div className="p-4 border-t flex justify-end">
              <button 
                onClick={() => setShowJobsModal(false)}
                className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Responsive styles
const styles = {
  container: {
    padding: '20px',
    maxWidth: '1200px',
    margin: '0 auto',
  },
  
  header: {
    marginBottom: '20px',
  },
  
  card: {
    marginBottom: '30px',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
  },
  
  formGroup: {
    display: 'grid',
    gap: '15px',
  },
  
  formItem: {
    marginBottom: '10px',
  },
  
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: '500',
  },
  
  select: {
    width: '100%',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ddd',
    fontSize: '14px',
  },
  
  submitButton: {
    marginTop: '15px',
    padding: '10px 20px',
    backgroundColor: '#2ecc71',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  
  filterGroup: {
    display: 'flex',
    gap: '20px',
    marginBottom: '20px',
    flexWrap: 'wrap',
  },
  
  filterItem: {
    minWidth: '200px',
    flex: '1',
  },
  
  calendarContainer: {
    marginBottom: '20px',
  },
  
  calendar: {
    display: 'grid',
    gridTemplateColumns: 'repeat(7, 1fr)',
    gap: '4px',
    marginTop: '10px',
  },
  
  weekCalendar: {
    display: 'grid',
    gridTemplateColumns: 'repeat(7, 1fr)',
    gap: '4px',
    marginTop: '10px',
    minHeight: '300px',
  },
  
  calendarHeader: {
    textAlign: 'center',
    fontWeight: 'bold',
    padding: '8px',
    backgroundColor: '#f8f9fa',
  },
}
