import React, { useState } from 'react';

const MaintenanceCalendar = () => {
  // Sample jobs data
  const [jobs, setJobs] = useState([
    {
      id: 1,
      ship: 'Ocean Voyager',
      component: 'Engine',
      type: 'Inspection',
      priority: 'High',
      status: 'In Progress',
      startDate: 'May 12, 2025',
      assignedTo: 'John Smith'
    },
    {
      id: 2,
      ship: 'Marine Express',
      component: 'Propeller',
      type: 'Repair',
      priority: 'Medium',
      status: 'Pending',
      startDate: 'May 14, 2025',
      assignedTo: 'Sarah Johnson'
    },
    {
      id: 3,
      ship: 'Pacific Carrier',
      component: 'Navigation System',
      type: 'Maintenance',
      priority: 'Low',
      status: 'Pending',
      startDate: 'May 18, 2025',
      assignedTo: 'Michael Brown'
    }
  ]);

  // State for calendar view
  const [currentView, setCurrentView] = useState('month'); // 'month' or 'week'
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedDateJobs, setSelectedDateJobs] = useState([]);

  // Get days in month for the calendar
  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    
    const days = [];
    const firstDayOfMonth = new Date(year, month, 1).getDay();
    
    // Add empty days for the start of the month
    for (let i = 0; i < firstDayOfMonth; i++) {
      days.push(null);
    }
    
    // Add days of the month
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(new Date(year, month, i));
    }
    
    return days;
  };

  // Navigate to previous/next month
  const navigateMonth = (direction) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + direction);
    setCurrentMonth(newMonth);
  };

  // Check if a date has jobs
  const getJobsForDate = (date) => {
    if (!date) return [];
    
    return jobs.filter(job => {
      const jobDate = new Date(job.startDate);
      return jobDate.getDate() === date.getDate() && 
             jobDate.getMonth() === date.getMonth() && 
             jobDate.getFullYear() === date.getFullYear();
    });
  };

  // Handle date click
  const handleDateClick = (date) => {
    if (!date) return;
    
    const dateJobs = getJobsForDate(date);
    setSelectedDate(date);
    setSelectedDateJobs(dateJobs);
  };

  // Format date for display
  const formatDate = (date) => {
    if (!date) return '';
    return date.toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' });
  };

  // Get days for the calendar based on current view
  const calendarDays = getDaysInMonth(currentMonth);

  // Get weekdays for the calendar header
  const weekdays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <div className="w-full">
      <div className="flex flex-col bg-white rounded-lg shadow mb-6">
        <div className="p-4 bg-blue-50 rounded-t-lg">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-xl font-semibold text-gray-800">Maintenance Calendar</h3>
            <div className="flex items-center space-x-2">
              <button 
                className={`px-3 py-1 text-sm rounded ${currentView === 'month' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
                onClick={() => setCurrentView('month')}
              >
                Month
              </button>
              <button 
                className={`px-3 py-1 text-sm rounded ${currentView === 'week' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
                onClick={() => setCurrentView('week')}
              >
                Week
              </button>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <h4 className="text-lg font-medium">
              {currentMonth.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
            </h4>
            <div className="flex space-x-2">
              <button 
                onClick={() => navigateMonth(-1)} 
                className="p-2 rounded hover:bg-gray-200"
              >
                ◀
              </button>
              <button 
                onClick={() => navigateMonth(1)} 
                className="p-2 rounded hover:bg-gray-200"
              >
                ▶
              </button>
            </div>
          </div>
        </div>
        
        {/* Calendar Grid */}
        <div className="p-4">
          {/* Calendar Header - Weekdays */}
          <div className="grid grid-cols-7 mb-2">
            {weekdays.map(day => (
              <div key={day} className="text-center font-medium text-gray-600 text-sm py-2">
                {day}
              </div>
            ))}
          </div>
          
          {/* Calendar Body - Days */}
          <div className="grid grid-cols-7 gap-1 md:gap-2">
            {calendarDays.map((day, index) => {
              const dayJobs = day ? getJobsForDate(day) : [];
              const isToday = day && new Date().toDateString() === day.toDateString();
              const isSelected = selectedDate && day && selectedDate.toDateString() === day.toDateString();
              
              return (
                <div
                  key={index}
                  className={`
                    border rounded min-h-12 md:min-h-24 p-1 md:p-2 relative cursor-pointer transition-colors
                    ${!day ? 'bg-gray-100' : 'hover:bg-blue-50'}
                    ${isToday ? 'border-blue-500' : 'border-gray-200'}
                    ${isSelected ? 'bg-blue-100' : ''}
                  `}
                  onClick={() => day && handleDateClick(day)}
                >
                  {day && (
                    <>
                      <div className={`text-right text-sm ${isToday ? 'font-bold text-blue-600' : ''}`}>
                        {day.getDate()}
                      </div>
                      
                      {dayJobs.length > 0 && (
                        <div className="hidden md:block mt-1">
                          {dayJobs.length <= 2 ? (
                            dayJobs.map(job => (
                              <div 
                                key={job.id}
                                className={`text-xs truncate rounded px-1 my-1
                                  ${job.priority === 'High' ? 'bg-red-100 text-red-800' : 
                                    job.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' : 
                                    'bg-green-100 text-green-800'}`}
                              >
                                {job.type}
                              </div>
                            ))
                          ) : (
                            <div className="text-xs font-medium bg-gray-100 rounded px-1 py-0.5 text-center">
                              {dayJobs.length} jobs
                            </div>
                          )}
                        </div>
                      )}
                      
                      {/* Mobile indicator - only show a dot */}
                      {dayJobs.length > 0 && (
                        <div className="md:hidden absolute bottom-1 right-1">
                          <div className={`h-2 w-2 rounded-full
                            ${dayJobs.some(j => j.priority === 'High') ? 'bg-red-500' : 
                              dayJobs.some(j => j.priority === 'Medium') ? 'bg-yellow-500' : 
                              'bg-green-500'}`}
                          />
                        </div>
                      )}
                    </>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>
      
      {/* Selected Date Jobs */}
      {selectedDate && (
        <div className="bg-white rounded-lg shadow p-4">
          <h3 className="text-lg font-medium mb-3">
            Jobs on {formatDate(selectedDate)}
          </h3>
          
          {selectedDateJobs.length === 0 ? (
            <p className="text-gray-500">No jobs scheduled for this date.</p>
          ) : (
            <div className="space-y-3">
              {selectedDateJobs.map(job => (
                <div 
                  key={job.id} 
                  className="border-l-4 p-3 bg-gray-50 rounded"
                  style={{ 
                    borderLeftColor: job.priority === 'High' ? '#e74c3c' : 
                      job.priority === 'Medium' ? '#f39c12' : '#2ecc71'
                  }}
                >
                  <div className="flex justify-between items-start">
                    <div>
                      <h4 className="font-medium">{job.type} - {job.component}</h4>
                      <p className="text-sm text-gray-600">{job.ship}</p>
                    </div>
                    <span 
                      className={`text-xs px-2 py-1 rounded-full
                        ${job.status === 'Completed' ? 'bg-green-100 text-green-800' : 
                          job.status === 'In Progress' ? 'bg-blue-100 text-blue-800' : 
                          'bg-gray-100 text-gray-800'}`}
                    >
                      {job.status}
                    </span>
                  </div>
                  <div className="mt-2 text-sm text-gray-500">
                    Assigned to: {job.assignedTo}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default MaintenanceCalendar;
