import React, { useState } from 'react';
import JobList from '../components/Jobs/JobList';
import JobForm from '../components/Jobs/JobForm';
import JobCalendar from '../components/Jobs/JobCalendar';
import { useJobs } from '../contexts/JobsContext';
import { useShips } from '../contexts/ShipsContext';
import { useEngineers } from '../contexts/EngineersContext';

const JobsPage = () => {
  const [showForm, setShowForm] = useState(false);
  const [view, setView] = useState('list'); // 'list' or 'calendar'
  const [currentJob, setCurrentJob] = useState(null);
  
  const { jobs, addJob, updateJob, updateJobStatus } = useJobs();
  const { ships } = useShips();
  const { engineers } = useEngineers();

  const handleAddJob = () => {
    setCurrentJob(null);
    setShowForm(true);
  };

  const handleEditJob = (job) => {
    setCurrentJob(job);
    setShowForm(true);
  };

  const handleSubmitJob = (jobData) => {
    if (currentJob) {
      updateJob(currentJob.id, jobData);
    } else {
      addJob(jobData);
    }
    setShowForm(false);
  };

  const handleStatusChange = (jobId, newStatus) => {
    updateJobStatus(jobId, newStatus);
  };

  return (
    <div className="jobs-page p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">Maintenance Jobs</h1>
        <div className="flex space-x-2">
          <button
            onClick={() => setView('list')}
            className={`px-4 py-2 rounded-md ${view === 'list' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
          >
            List View
          </button>
          <button
            onClick={() => setView('calendar')}
            className={`px-4 py-2 rounded-md ${view === 'calendar' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
          >
            Calendar View
          </button>
          <button
            onClick={handleAddJob}
            className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700"
          >
            Add New Job
          </button>
        </div>
      </div>

      {showForm && (
        <div className="mb-6">
          <JobForm 
            onSubmit={handleSubmitJob}
            initialData={currentJob || {}}
            ships={ships}
            engineers={engineers}
            onCancel={() => setShowForm(false)}
          />
        </div>
      )}

      {view === 'list' ? (
        <JobList 
          jobs={jobs}
          ships={ships}
          onEdit={handleEditJob}
          onStatusChange={handleStatusChange}
          onAddJob={handleAddJob}
        />
      ) : (
        <JobCalendar />
      )}
    </div>
  );
};

export default JobsPage;
