import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useShips } from '../../contexts/ShipsContext';
import { useAuth } from '../../contexts/AuthContext';

const ShipList = ({ onEdit }) => {
  const navigate = useNavigate();
  const { ships, deleteShip } = useShips();
  const { user } = useAuth();

  const handleShipClick = (shipId) => {
    navigate(`/ships/${shipId}`);
  };

  return (
    <div className="ship-list">
      <h2>Ships</h2>
      <table className="ship-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>IMO Number</th>
            <th>Flag</th>
            <th>Status</th>
            <th>Last Maintenance</th>
            <th>Next Maintenance</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {ships.map((ship) => (
            <tr key={ship.id}>
              <td data-label="Name" style={{ cursor: 'pointer', color: '#1976d2' }} onClick={() => handleShipClick(ship.id)}>
                <span>{ship.name}</span>
                <br />
                <span style={{ fontSize: '0.85em', color: '#64748b', marginTop: 2 }}>{ship.description}</span>
              </td>
              <td data-label="IMO Number">{ship.imoNumber}</td>
              <td data-label="Flag">{ship.flag}</td>
              <td data-label="Status">{ship.status}</td>
              <td data-label="Last Maintenance">{ship.lastMaintenance || '-'}</td>
              <td data-label="Next Maintenance">{ship.nextMaintenance || '-'}</td>
              <td data-label="Actions">
                {user?.role === 'Admin' && <button onClick={() => onEdit(ship)}>Edit</button>}
                <button onClick={() => deleteShip(ship.id)} style={{ marginLeft: 8, color: 'red' }}>Delete</button>
                <button onClick={() => handleShipClick(ship.id)} style={{ marginLeft: 8 }}>Ship Details</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ShipList;
