// ComponentsManagement.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import ComponentForm from './ComponentForm';

const ComponentsManagement = () => {
  const { shipId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { theme } = useTheme();
  const [components, setComponents] = useState([]);
  const [editingComponent, setEditingComponent] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [selectedComponent, setSelectedComponent] = useState(null);

  // Fetch components for the current ship
  useEffect(() => {
    const fetchComponents = async () => {
      try {
        // Simulate API call with localStorage
        const storedComponents = JSON.parse(localStorage.getItem('components')) || [];
        const shipComponents = storedComponents.filter(comp => comp.shipId === shipId);
        setComponents(shipComponents);
      } catch (error) {
        console.error('Error fetching components:', error);
      }
    };

    if (shipId) {
      fetchComponents();
    }
  }, [shipId]);

  const handleAddComponent = (newComponent) => {
    const updatedComponents = [...components, newComponent];
    setComponents(updatedComponents);
    updateLocalStorage(updatedComponents);
    setShowForm(false);
  };

  const handleUpdateComponent = (updatedComponent) => {
    const updatedComponents = components.map(comp => 
      comp.id === updatedComponent.id ? updatedComponent : comp
    );
    setComponents(updatedComponents);
    updateLocalStorage(updatedComponents);
    setEditingComponent(null);
    setShowForm(false);
  };

  const handleDelete = (id) => {
    const updatedComponents = components.filter(comp => comp.id !== id);
    setComponents(updatedComponents);
    updateLocalStorage(updatedComponents);
    if (selectedComponent?.id === id) {
      setSelectedComponent(null);
    }
  };

  const updateLocalStorage = (comps) => {
    const allComponents = JSON.parse(localStorage.getItem('components')) || [];
    const otherComponents = allComponents.filter(comp => comp.shipId !== shipId);
    localStorage.setItem('components', JSON.stringify([...otherComponents, ...comps]));
  };

  return (
    <div className={`components-management ${theme}`}>
      <div className="components-container">
        {/* Components List */}
        <div className="components-list">
          <div className="list-header">
            <h2>Ship Components</h2>
            <button 
              onClick={() => {
                setEditingComponent(null);
                setShowForm(true);
              }}
              className="add-button"
            >
              + Add Component
            </button>
          </div>

          <div className="components-grid">
            {components.length > 0 ? (
              components.map(component => (
                <div 
                  key={component.id} 
                  className={`component-card ${selectedComponent?.id === component.id ? 'selected' : ''}`}
                  onClick={() => setSelectedComponent(component)}
                >
                  <h3>{component.name}</h3>
                  <p>Serial: {component.serialNumber}</p>
                  <div className="component-actions">
                    <button 
                      onClick={(e) => {
                        e.stopPropagation();
                        setEditingComponent(component);
                        setShowForm(true);
                      }}
                      className="edit-btn"
                    >
                      Edit
                    </button>
                    <button 
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDelete(component.id);
                      }}
                      className="delete-btn"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <p className="no-components">No components found for this ship.</p>
            )}
          </div>
        </div>

        {/* Component Details */}
        <div className="component-details">
          {selectedComponent ? (
            <>
              <h2>Component Details</h2>
              <div className="detail-item">
                <strong>Name:</strong>
                <span>{selectedComponent.name}</span>
              </div>
              <div className="detail-item">
                <strong>Serial Number:</strong>
                <span>{selectedComponent.serialNumber}</span>
              </div>
              <div className="detail-item">
                <strong>Installation Date:</strong>
                <span>{new Date(selectedComponent.installationDate).toLocaleDateString()}</span>
              </div>
              <div className="detail-item">
                <strong>Last Maintenance:</strong>
                <span>{new Date(selectedComponent.lastMaintenanceDate).toLocaleDateString()}</span>
              </div>
              <div className="detail-item">
                <strong>Status:</strong>
                <span className={`status ${selectedComponent.status.toLowerCase()}`}>
                  {selectedComponent.status}
                </span>
              </div>
            </>
          ) : (
            <div className="no-selection">
              <p>Select a component to view details</p>
            </div>
          )}
        </div>
      </div>

      {/* Component Form Modal */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <ComponentForm
              shipId={shipId}
              component={editingComponent}
              onAdd={handleAddComponent}
              onUpdate={handleUpdateComponent}
              onCancel={() => setShowForm(false)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default ComponentsManagement;
