import React, { useState } from 'react';

const MaintenanceJobs = () => {
  // Sample engineers data
  const engineers = [
    { id: 1, name: 'John Smith' },
    { id: 2, name: 'Sarah Johnson' },
    { id: 3, name: 'Michael Brown' }
  ];

  // Sample ships data
  const ships = [
    { id: 1, name: 'Ocean Voyager' },
    { id: 2, name: 'Marine Express' },
    { id: 3, name: 'Pacific Carrier' }
  ];

  // Sample components data
  const components = [
    { id: 1, name: 'Engine' },
    { id: 2, name: 'Propeller' },
    { id: 3, name: 'Navigation System' },
    { id: 4, name: 'Cooling System' }
  ];

  // Job types
  const jobTypes = ['Inspection', 'Repair', 'Maintenance', 'Replacement'];

  // State for jobs and form
  const [jobs, setJobs] = useState([
    {
      id: 1,
      ship: 'Ocean Voyager',
      component: 'Engine',
      type: 'Inspection',
      priority: 'High',
      status: 'In Progress',
      startDate: 'May 12, 2025',
      assignedTo: 'John Smith'
    }
  ]);

  const [formData, setFormData] = useState({
    ship: '',
    component: '',
    type: '',
    priority: 'Medium',
    assignedTo: ''
  });

  // Filter states
  const [filterShip, setFilterShip] = useState('All Ships');
  const [filterStatus, setFilterStatus] = useState('All Statuses');
  const [filterPriority, setFilterPriority] = useState('All Priorities');

  // Create new job
  const createJob = (e) => {
    e.preventDefault();
    const newJob = {
      id: jobs.length + 1,
      ship: formData.ship,
      component: formData.component,
      type: formData.type,
      priority: formData.priority,
      status: 'Pending',
      startDate: new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' }),
      assignedTo: formData.assignedTo
    };
    setJobs([...jobs, newJob]);
    setFormData({
      ship: '',
      component: '',
      type: '',
      priority: 'Medium',
      assignedTo: ''
    });
  };

  // Update job status
  const updateJobStatus = (jobId, newStatus) => {
    setJobs(jobs.map(job => 
      job.id === jobId ? { ...job, status: newStatus } : job
    ));
  };

  // Filter jobs
  const filteredJobs = jobs.filter(job => {
    return (
      (filterShip === 'All Ships' || job.ship === filterShip) &&
      (filterStatus === 'All Statuses' || job.status === filterStatus) &&
      (filterPriority === 'All Priorities' || job.priority === filterPriority)
    );
  });

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800 sm:text-center md:text-left">Maintenance Jobs Management</h1>
      
      {/* Create Job Form */}
      <div className="bg-white shadow rounded-lg p-4 sm:p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4">Create New Maintenance Job</h2>
        <form onSubmit={createJob}>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Ship:</label>
              <select
                value={formData.ship}
                onChange={(e) => setFormData({...formData, ship: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Select Ship</option>
                {ships.map(ship => (
                  <option key={ship.id} value={ship.name}>{ship.name}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Component:</label>
              <select
                value={formData.component}
                onChange={(e) => setFormData({...formData, component: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Select Component</option>
                {components.map(component => (
                  <option key={component.id} value={component.name}>{component.name}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Job Type:</label>
              <select
                value={formData.type}
                onChange={(e) => setFormData({...formData, type: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Select Type</option>
                {jobTypes.map((type, index) => (
                  <option key={index} value={type}>{type}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Priority:</label>
              <select
                value={formData.priority}
                onChange={(e) => setFormData({...formData, priority: e.target.value})}
                className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Assigned Engineer:</label>
              <select
                value={formData.assignedTo}
                onChange={(e) => setFormData({...formData, assignedTo: e.target.value})}
                required
                className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Select Engineer</option>
                {engineers.map(engineer => (
                  <option key={engineer.id} value={engineer.name}>{engineer.name}</option>
                ))}
              </select>
            </div>
          </div>
          <button 
            type="submit"
            className="mt-4 w-full sm:w-auto px-4 py-2 bg-green-500 text-white font-medium rounded-md hover:bg-green-600 transition-colors"
          >
            Create Job
          </button>
        </form>
      </div>

      {/* Jobs List with Filters */}
      <div>
        <h2 className="text-2xl font-bold mb-4">Maintenance Jobs</h2>
        <div className="flex flex-col sm:flex-row gap-4 mb-6">
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Filter by Ship:</label>
            <select 
              value={filterShip} 
              onChange={(e) => setFilterShip(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-md text-sm"
            >
              <option value="All Ships">All Ships</option>
              {ships.map(ship => (
                <option key={ship.id} value={ship.name}>{ship.name}</option>
              ))}
            </select>
          </div>

          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Filter by Status:</label>
            <select 
              value={filterStatus} 
              onChange={(e) => setFilterStatus(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-md text-sm"
            >
              <option value="All Statuses">All Statuses</option>
              <option value="Pending">Pending</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>

          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Filter by Priority:</label>
            <select 
              value={filterPriority} 
              onChange={(e) => setFilterPriority(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-md text-sm"
            >
              <option value="All Priorities">All Priorities</option>
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
          </div>
        </div>
        
        {/* Calendar View - Hidden on small screens */}
        <div className="hidden md:block mb-8">
          <h3 className="text-lg font-semibold mb-2">Maintenance Calendar</h3>
          <div className="grid grid-cols-7 gap-1">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="text-center font-medium py-2 bg-gray-100">
                {day}
              </div>
            ))}
            {Array.from({ length: 35 }, (_, i) => {
              const date = new Date();
              date.setDate(i + 1);
              const dayJobs = jobs.filter(job => new Date(job.startDate).getDate() === date.getDate());
              
              return (
                <div
                  key={i}
                  className={`border p-2 min-h-16 cursor-pointer transition-colors ${dayJobs.length ? 'bg-gray-50' : 'bg-white'}`}
                  onClick={() => {
                    if (dayJobs.length) {
                      alert(`Jobs on ${date.toLocaleDateString()}:\n${dayJobs.map(job => 
                        `- ${job.type} of ${job.component} on ${job.ship}`
                      ).join('\n')}`);
                    }
                  }}
                >
                  <div className="font-bold mb-1">{date.getDate()}</div>
                  {dayJobs.length > 0 && (
                    <div className="text-xs text-gray-600">
                      {dayJobs.length} job{dayJobs.length > 1 ? 's' : ''}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
        
        {/* Jobs Table */}
        <div className="overflow-x-auto bg-white shadow rounded-lg">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider hidden sm:table-cell">Job ID</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ship</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Component</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider hidden md:table-cell">Type</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Priority</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider hidden lg:table-cell">Start Date</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider hidden md:table-cell">Assigned To</th>
                <th scope="col" className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredJobs.length > 0 ? (
                filteredJobs.map(job => (
                  <tr key={job.id} className="hover:bg-gray-50">
                    <td className="px-4 py-4 whitespace-nowrap text-sm hidden sm:table-cell">#{job.id}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">{job.ship}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">{job.component}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm hidden md:table-cell">{job.type}</td>
                    <td className="px-4 py-4 whitespace-nowrap">
                      <span className={`text-sm font-medium px-2 py-1 rounded-full ${
                        job.priority === 'High' ? 'bg-red-100 text-red-800' : 
                        job.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' : 
                        'bg-green-100 text-green-800'
                      }`}>
                        {job.priority}
                      </span>
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      <span className={`px-2 py-1 rounded-full ${
                        job.status === 'Completed' ? 'bg-green-100 text-green-800' : 
                        job.status === 'In Progress' ? 'bg-blue-100 text-blue-800' : 
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {job.status}
                      </span>
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm hidden lg:table-cell">{job.startDate}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm hidden md:table-cell">{job.assignedTo}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      {job.status !== 'Completed' && (
                        <div className="flex space-x-2">
                          <button 
                            onClick={() => updateJobStatus(job.id, 'In Progress')}
                            className="bg-blue-500 text-white px-2 py-1 rounded text-xs disabled:opacity-50"
                            disabled={job.status === 'In Progress'}
                          >
                            Start
                          </button>
                          <button 
                            onClick={() => updateJobStatus(job.id, 'Completed')}
                            className="bg-green-500 text-white px-2 py-1 rounded text-xs"
                          >
                            Complete
                          </button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="9" className="px-4 py-8 text-center text-gray-500">
                    No jobs found matching your filters
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default MaintenanceJobs;
