import React, { useState, useRef, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef();
  const hamburgerRef = useRef();

  // Close menu on outside click
  useEffect(() => {
    if (!menuOpen) return;
    function handleClick(e) {
      if (
        menuRef.current &&
        !menuRef.current.contains(e.target) &&
        hamburgerRef.current &&
        !hamburgerRef.current.contains(e.target)
      ) {
        setMenuOpen(false);
      }
    }
    document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [menuOpen]);

  const handleLogout = () => {
    logout();
    setMenuOpen(false);
    navigate('/signup');
  };

  const handleNavClick = (to) => {
    setMenuOpen(false);
    navigate(to);
  };

  return (
    <nav className="navbar">
      <span className="navbar-logo" onClick={() => handleNavClick('/dashboard')} style={{ cursor: 'pointer', fontWeight: 700, fontSize: '1.3rem', color: '#fff' }}>
        ğŸš¢ ShipMaster
      </span>
      {/* Desktop/Tablet nav links */}
      <div className="navbar-links-desktop">
        {user && (
          <>
            <span onClick={() => handleNavClick('/dashboard')} className="nav-link" style={{ cursor: 'pointer' }}>Dashboard</span>
            <span onClick={() => handleNavClick('/ships')} className="nav-link" style={{ cursor: 'pointer' }}>Ship Management</span>
            <span onClick={() => handleNavClick('/jobs')} className="nav-link" style={{ cursor: 'pointer' }}>Jobs</span>
          </>
        )}
        <button onClick={toggleTheme} className="nav-btn">
          {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
        </button>
        {user && (
          <button onClick={handleLogout} className="nav-btn">Logout</button>
        )}
      </div>
      {/* Hamburger for mobile */}
      <button
        className="navbar-toggle"
        aria-label="Toggle navigation menu"
        aria-expanded={menuOpen}
        onClick={() => setMenuOpen((open) => !open)}
        ref={hamburgerRef}
      >
        <span className="navbar-toggle-bar" />
        <span className="navbar-toggle-bar" />
        <span className="navbar-toggle-bar" />
      </button>
      {/* Mobile dropdown menu */}
      <div className={`navbar-dropdown${menuOpen ? ' open' : ''}`} ref={menuRef}>
        {user && (
          <>
            <span onClick={() => handleNavClick('/dashboard')} className="nav-link" style={{ cursor: 'pointer' }}>Dashboard</span>
            <span onClick={() => handleNavClick('/ships')} className="nav-link" style={{ cursor: 'pointer' }}>Ship Management</span>
            <span onClick={() => handleNavClick('/jobs')} className="nav-link" style={{ cursor: 'pointer' }}>Jobs</span>
          </>
        )}
        <button onClick={() => { toggleTheme(); setMenuOpen(false); }} className="nav-btn">
          {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
        </button>
        {user && (
          <button onClick={handleLogout} className="nav-btn">Logout</button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
