import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef();
  const hamburgerRef = useRef();

  // Close menu on outside click or Escape
  useEffect(() => {
    if (!menuOpen) return;
    function handleClick(e) {
      if (
        menuRef.current &&
        !menuRef.current.contains(e.target) &&
        hamburgerRef.current &&
        !hamburgerRef.current.contains(e.target)
      ) {
        setMenuOpen(false);
      }
    }
    function handleEsc(e) {
      if (e.key === 'Escape') setMenuOpen(false);
    }
    document.addEventListener('mousedown', handleClick);
    document.addEventListener('keydown', handleEsc);
    return () => {
      document.removeEventListener('mousedown', handleClick);
      document.removeEventListener('keydown', handleEsc);
    };
  }, [menuOpen]);

  // Trap focus in menu when open (basic)
  useEffect(() => {
    if (!menuOpen) return;
    const firstBtn = menuRef.current?.querySelector('button');
    firstBtn && firstBtn.focus();
  }, [menuOpen]);

  const handleLogout = () => {
    logout();
    setMenuOpen(false);
    navigate('/signup');
  };

  const handleNavClick = (to) => {
    setMenuOpen(false);
    navigate(to);
  };

  return (
    <nav className="navbar" role="navigation" aria-label="Main Navigation">
      <span
        className="navbar-logo"
        onClick={() => handleNavClick('/dashboard')}
        style={{ cursor: 'pointer', fontWeight: 700, fontSize: '1.3rem', color: '#fff' }}
        tabIndex={0}
        aria-label="ShipMaster Home"
        onKeyDown={e => (e.key === 'Enter' || e.key === ' ') && handleNavClick('/dashboard')}
      >
        🚢 ShipMaster
      </span>
      {/* Desktop nav links */}
      <ul className="navbar-links">
        {user && (
          <>
            <li><button onClick={() => handleNavClick('/dashboard')} className="nav-link">Dashboard</button></li>
            <li><button onClick={() => handleNavClick('/ships')} className="nav-link">Ship Management</button></li>
            <li><button onClick={() => handleNavClick('/jobs')} className="nav-link">Jobs</button></li>
          </>
        )}
        <li><button onClick={toggleTheme} className="nav-btn">{theme === 'light' ? 'Dark Mode' : 'Light Mode'}</button></li>
        {user && (
          <li><button onClick={handleLogout} className="nav-btn">Logout</button></li>
        )}
      </ul>
      {/* Hamburger for mobile/tablet */}
      <button
        className="navbar-toggle"
        aria-label={menuOpen ? 'Close navigation menu' : 'Open navigation menu'}
        aria-expanded={menuOpen}
        aria-controls="navbar-links-mobile"
        onClick={() => setMenuOpen((open) => !open)}
        ref={hamburgerRef}
        tabIndex={0}
      >
        <span className="navbar-toggle-bar" />
        <span className="navbar-toggle-bar" />
        <span className="navbar-toggle-bar" />
      </button>
      {/* Mobile/tablet dropdown menu */}
      <ul
        id="navbar-links-mobile"
        className={`navbar-links-mobile${menuOpen ? ' open' : ''}`}
        ref={menuRef}
        role="menu"
        aria-label="Mobile Navigation"
      >
        {user && (
          <>
            <li><button onClick={() => handleNavClick('/dashboard')} className="nav-link" role="menuitem" tabIndex={menuOpen ? 0 : -1}>Dashboard</button></li>
            <li><button onClick={() => handleNavClick('/ships')} className="nav-link" role="menuitem" tabIndex={menuOpen ? 0 : -1}>Ship Management</button></li>
            <li><button onClick={() => handleNavClick('/jobs')} className="nav-link" role="menuitem" tabIndex={menuOpen ? 0 : -1}>Jobs</button></li>
          </>
        )}
        <li><button onClick={() => { toggleTheme(); setMenuOpen(false); }} className="nav-btn" role="menuitem" tabIndex={menuOpen ? 0 : -1}>{theme === 'light' ? 'Dark Mode' : 'Light Mode'}</button></li>
        {user && (
          <li><button onClick={handleLogout} className="nav-btn" role="menuitem" tabIndex={menuOpen ? 0 : -1}>Logout</button></li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
