import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const NAV_ITEMS = [
  { label: 'Dashboard', path: '/dashboard' },
  { label: 'Ship Management', path: '/ships' },
  { label: 'Jobs', path: '/jobs' },
  { label: 'Components', path: '/components/' },
];

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const menuRef = useRef();
  const hamburgerRef = useRef();

  const isActive = (path) => location.pathname.startsWith(path);

  const isMobile = () => window.innerWidth <= 1024;

  useEffect(() => {
    if (!menuOpen) return;
    function handleClick(e) {
      if (
        menuRef.current &&
        !menuRef.current.contains(e.target) &&
        hamburgerRef.current &&
        !hamburgerRef.current.contains(e.target)
      ) {
        setMenuOpen(false);
      }
    }
    function handleEsc(e) {
      if (e.key === 'Escape') setMenuOpen(false);
    }
    document.addEventListener('mousedown', handleClick);
    document.addEventListener('keydown', handleEsc);
    return () => {
      document.removeEventListener('mousedown', handleClick);
      document.removeEventListener('keydown', handleEsc);
    };
  }, [menuOpen]);

  useEffect(() => {
    if (!menuOpen) return;
    const firstBtn = menuRef.current?.querySelector('button, [tabindex="0"]');
    firstBtn && firstBtn.focus();
  }, [menuOpen]);

  const handleNavClick = (to) => {
    setMenuOpen(false);
    navigate(to);
  };

  const handleLogout = () => {
    setShowLogoutConfirm(true);
    setMenuOpen(false);
  };

  const confirmLogout = () => {
    logout();
    setShowLogoutConfirm(false);
    navigate('/signup');
  };

  const cancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  const styles = {
    nav: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '0.5rem 1rem',
      backgroundColor: '#2c3e50',
      color: '#ecf0f1',
      position: 'relative',
      flexWrap: 'wrap',
    },
    logo: {
      cursor: 'pointer',
      fontWeight: 700,
      fontSize: '1.3rem',
      color: '#fff',
    },
    linkList: {
      display: isMobile() ? 'none' : 'flex',
      listStyle: 'none',
      margin: 0,
      padding: 0,
      gap: '1rem',
      alignItems: 'center',
    },
    linkButton: {
      backgroundColor: 'rgba(255, 255, 255, 0.85)',
      border: 'none',
      borderRadius: '10px',
      color: '#d0d4d9',
      fontSize: '1rem',
      fontWeight: 'bold',
      cursor: 'pointer',
      padding: '0.5rem 1.5rem',
      boxShadow: '0 8px 20px rgba(206, 212, 218, 0.3)',
      transition: 'all 0.3s ease-in-out',
    },
    activeLink: {
      fontWeight: 'bold',
      textDecoration: 'underline',
    },
    toggleButton: {
      display: isMobile() ? 'flex' : 'none',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '30px',
      cursor: 'pointer',
      background: 'none',
      border: 'none',
    },
    toggleBar: {
      width: '100%',
      height: '3px',
      backgroundColor: '#ecf0f1',
      margin: '3px 0',
    },
    mobileMenu: {
      display: menuOpen ? 'block' : 'none',
      position: 'absolute',
      top: '100%',
      left: 0,
      width: '100%',
      backgroundColor: '#2c3e50',
      padding: '1rem 0',
      boxShadow: '0 8px 32px rgba(44,62,80,0.18)',
    },
    mobileList: {
      listStyle: 'none',
      padding: 0,
      margin: 0,
      display: 'flex',
      flexDirection: 'column',
      gap: '1rem',
    },
    logoutOverlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: 'rgba(255, 255, 255, 0.9)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
    },
    logoutDialog: {
      backgroundColor: 'rgba(255, 255, 255, 0.75)',
      padding: '2rem',
      borderRadius: '20px',
      textAlign: 'center',
      boxShadow: '0 15px 30px rgba(200, 220, 255, 0.3)',
      backdropFilter: 'blur(12px)',
      WebkitBackdropFilter: 'blur(12px)',
      color: '#dee2e6',
      border: '1px solid rgba(255, 255, 255, 0.3)',
    },
    logoutActions: {
      marginTop: '1rem',
      display: 'flex',
      justifyContent: 'center',
      gap: '2rem',
    },
  };

  return (
    <nav style={styles.nav}>
      <span style={styles.logo} onClick={() => handleNavClick('/dashboard')}>ðŸš¢ ShipMaster</span>

      <ul style={styles.linkList}>
        {user && NAV_ITEMS.map(item => (
          <li key={item.path}>
            <button
              onClick={() => handleNavClick(item.path)}
              style={{
                ...styles.linkButton,
                ...(isActive(item.path) ? styles.activeLink : {}),
              }}
            >
              {item.label}
            </button>
          </li>
        ))}
        <li>
          <button onClick={toggleTheme} style={styles.linkButton}>
            {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
          </button>
        </li>
        {user && (
          <li>
            <button onClick={handleLogout} style={styles.linkButton}>Logout</button>
          </li>
        )}
      </ul>

      <button
        onClick={() => setMenuOpen(open => !open)}
        ref={hamburgerRef}
        style={styles.toggleButton}
      >
        <span style={styles.toggleBar} />
        <span style={styles.toggleBar} />
        <span style={styles.toggleBar} />
      </button>

      <div ref={menuRef} style={styles.mobileMenu}>
        <ul style={styles.mobileList}>
          {user && NAV_ITEMS.map(item => (
            <li key={item.path}>
              <button onClick={() => handleNavClick(item.path)} style={styles.linkButton}>
                {item.label}
              </button>
            </li>
          ))}
          <li>
            <button onClick={() => { toggleTheme(); setMenuOpen(false); }} style={styles.linkButton}>
              {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
            </button>
          </li>
          {user && (
            <li>
              <button onClick={handleLogout} style={styles.linkButton}>Logout</button>
            </li>
          )}
        </ul>
      </div>

      {showLogoutConfirm && (
        <div style={styles.logoutOverlay}>
          <div style={styles.logoutDialog}>
            <p style={{ fontSize: '1.5rem', color: '#000000' }}>Are you sure you want to logout?</p>

            <div style={styles.logoutActions}>
              <button onClick={confirmLogout} style={styles.linkButton}>Yes, Logout</button>
              <button onClick={cancelLogout} style={styles.linkButton}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
