import React, { useState, useEffect } from 'react';
import KPICards from './KPICards';
import JobsStatusChart from './Charts/JobsStatusChart';
import MaintenanceStatusChart from './Charts/MaintenanceStatusChart';
import './Dashboard.css';

const Dashboard = () => {
  const [kpiData, setKpiData] = useState(null);

  useEffect(() => {
    // Simulate API fetch
    const fetchData = () => {
      setKpiData({
        totalShips: Math.floor(Math.random() * 20) + 5,
        overdueComponents: Math.floor(Math.random() * 10) + 1,
        jobsInProgress: Math.floor(Math.random() * 30) + 5,
        jobsCompleted: Math.floor(Math.random() * 50) + 10
      });
    };

    fetchData();
    const interval = setInterval(fetchData, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="dashboard">
      <h1>Maintenance Dashboard</h1>
      {kpiData && (
        <>
          <KPICards kpiData={kpiData} />
          <div className="charts-grid">
            <JobsStatusChart 
              inProgress={kpiData.jobsInProgress} 
              completed={kpiData.jobsCompleted} 
            />
            <MaintenanceStatusChart 
              overdue={kpiData.overdueComponents} 
              totalShips={kpiData.totalShips} 
            />
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
