import React from 'react';
import KPICards from '../components/Dashboard/KPICards';
import Charts from '../components/Dashboard/Charts';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';

const DashboardPage = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const { theme, toggleTheme } = useTheme();

  const handleLogout = () => {
    logout();
    navigate('/signup');
  };

  return (
    <div className="dashboard-page">
      <h1>Dashboard</h1>
      {user && <p style={{marginBottom: '1rem'}}>Logged in as <b>{user.email}</b> (<b>{user.role}</b>)</p>}
      <button onClick={handleLogout} style={{marginBottom: '1.5rem'}}>Logout</button>
      <button onClick={toggleTheme} style={{marginLeft: '1rem', marginBottom: '1.5rem', background: '#4f5b93', color: '#fff'}}>
        {theme === 'light' ? 'Dark Mode' : 'Light Mode'}
      </button>
      <KPICards />
      <Charts />
    </div>
  );
};

export default DashboardPage;
