import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { ShipsProvider } from './contexts/ShipsContext';
import { ComponentsProvider } from './contexts/ComponentsContext';
import { JobsProvider } from './contexts/JobsContext';
import { ThemeProvider } from './contexts/ThemeContext';
import Navbar from './components/Navbar';

import ComponentsPage from './components/Components/ComponentsPage';


// Pages
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/DashboardPage';
import ShipsPage from './pages/ShipsPage';
import ShipDetailPage from './pages/ShipDetailPage';
import JobsPage from './pages/JobsPage';
import SignupPage from './pages/SignupPage';

const PrivateRoute = ({ element }) => {
  const { user } = useAuth();
  return user ? element : <Navigate to="/login" replace />;
};

const AppContent = () => {
  const { user } = useAuth();
  const location = useLocation();
  const hideNavbar = location.pathname === '/login' || location.pathname === '/signup';
  return (
    <>
      {user && !hideNavbar && <Navbar />}
      <div className="app">
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/signup" element={<SignupPage />} />
          <Route path="/dashboard" element={<PrivateRoute element={<DashboardPage />} />} />
          <Route path="/ships" element={<PrivateRoute element={<ShipsPage />} />} />
          <Route path="/ships/:id" element={<PrivateRoute element={<ShipDetailPage />} />} />
          <Route path="/jobs" element={<PrivateRoute element={<JobsPage />} />} />
          <Route path="/" element={<Navigate to="/login" replace />} />
          <Route path="/components" element={<PrivateRoute element={<ComponentsPage />} />} />


          </Routes>
      </div>
    </>
  );
};

const App = () => (
  <ThemeProvider>
    <Router>
      <AuthProvider>
        <ShipsProvider>
          <ComponentsProvider>
            <JobsProvider>
              <AppContent />
            </JobsProvider>
          </ComponentsProvider>
        </ShipsProvider>
      </AuthProvider>
    </Router>
  </ThemeProvider>
);

export default App;
