import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const NAV_ITEMS = [
  { label: 'Dashboard', path: '/dashboard' },
  { label: 'Ship Management', path: '/ships' },
  { label: 'Jobs', path: '/jobs' },
  { label: 'Components', path: '/components/' },
];

const BREAKPOINTS = {
  mobile: 768,
  tablet: 1024,
};

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [screenSize, setScreenSize] = useState('desktop');
  const menuRef = useRef();
  const hamburgerRef = useRef();

  const getScreenSize = () => {
    const width = window.innerWidth;
    if (width <= BREAKPOINTS.mobile) return 'mobile';
    if (width <= BREAKPOINTS.tablet) return 'tablet';
    return 'desktop';
  };

  useEffect(() => {
    const handleResize = () => {
      const newSize = getScreenSize();
      setScreenSize(newSize);
      if (newSize === 'desktop') setMenuOpen(false);
    };

    handleResize(); // Initial check
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (!menuOpen) return;

    const handleClick = (e) => {
      if (!menuRef.current?.contains(e.target) && !hamburgerRef.current?.contains(e.target)) {
        setMenuOpen(false);
      }
    };

    const handleEsc = (e) => {
      if (e.key === 'Escape') setMenuOpen(false);
    };

    document.addEventListener('mousedown', handleClick);
    document.addEventListener('keydown', handleEsc);
    return () => {
      document.removeEventListener('mousedown', handleClick);
      document.removeEventListener('keydown', handleEsc);
    };
  }, [menuOpen]);
// Add this function before the return statement, after the styles object
const confirmLogout = async () => {
  try {
    await logout();
    setShowLogoutConfirm(false);
    navigate('/login');
  } catch (error) {
    console.error('Logout failed:', error);
  }
};

// ...rest of your code remains the same
  const NAV_ITEMS = [
    { label: 'Dashboard', path: '/dashboard' },
    { label: 'Ship Management', path: '/ships' },
    { label: 'Jobs', path: '/jobs' },
    { label: 'Components', path: '/components/' },
    { label: 'Maintenance Calendar', path: '/maintenance-calendar' }, // Added this line
  ];

  return (
    <nav style={styles.nav}>
      <span style={styles.logo} onClick={() => navigate('/dashboard')}>
        üö¢ ShipMaster
      </span>

      <button
        ref={hamburgerRef}
        style={styles.hamburger}
        onClick={() => setMenuOpen(!menuOpen)}
        aria-label="Menu"
      >
        <span style={{
          ...styles.hamburgerBar,
          transform: menuOpen ? 'rotate(45deg)' : 'rotate(0)',
        }} />
        <span style={{
          ...styles.hamburgerBar,
          opacity: menuOpen ? '0' : '1',
        }} />
        <span style={{
          ...styles.hamburgerBar,
          transform: menuOpen ? 'rotate(-45deg)' : 'rotate(0)',
        }} />
      </button>

      <ul style={styles.linkList} ref={menuRef}>
        {NAV_ITEMS.map(item => (
          <li key={item.path}>
            <button
              onClick={() => {
                navigate(item.path);
                setMenuOpen(false);
              }}
              style={{
                ...styles.linkButton,
                ...(location.pathname.startsWith(item.path) ? styles.activeLink : {}),
              }}
            >
              {item.label}
            </button>
          </li>
        ))}
        <li>
          <button
            onClick={() => {
              toggleTheme();
              setMenuOpen(false);
            }}
            style={styles.linkButton}
          >
            {theme === 'light' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
          </button>
        </li>
        {user && (
          <li>
            <button
              onClick={() => {
                setShowLogoutConfirm(true);
                setMenuOpen(false);
              }}
              style={styles.linkButton}
            >
              Logout
            </button>
          </li>
        )}
      </ul>

      {showLogoutConfirm && (
        <div style={styles.logoutOverlay}>
          <div style={styles.logoutDialog}>
            <h2>Confirm Logout</h2>
            <p>Are you sure you want to logout?</p>
            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginTop: '1.5rem' }}>
              <button
                onClick={confirmLogout}
                style={{
                  ...styles.linkButton,
                  backgroundColor: '#e74c3c',
                }}
              >
                Yes, Logout
              </button>
              <button
                onClick={() => setShowLogoutConfirm(false)}
                style={styles.linkButton}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
