import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { 
  FaShip, 
  FaWrench, 
  FaTasks, 
  FaCheckCircle,
  FaExclamationTriangle
} from 'react-icons/fa';
import './KPIDashboard.css';

// Generate random data for demonstration
const getRandomData = () => {
  const totalShips = Math.floor(Math.random() * 20) + 5;
  const overdueComponents = Math.floor(Math.random() * totalShips) + 1;
  const totalJobs = Math.floor(Math.random() * 50) + 10;
  const jobsInProgress = Math.floor(totalJobs * 0.6);
  const jobsCompleted = totalJobs - jobsInProgress;
  
  return {
    totalShips,
    overdueComponents,
    totalJobs,
    jobsInProgress,
    jobsCompleted
  };
};

const KPIDashboard = () => {
  const data = getRandomData();

  // Chart data for jobs status
  const jobsChartData = {
    labels: ['In Progress', 'Completed'],
    datasets: [
      {
        label: 'Jobs Status',
        data: [data.jobsInProgress, data.jobsCompleted],
        backgroundColor: [
          'rgba(52, 152, 219, 0.7)',
          'rgba(46, 204, 113, 0.7)'
        ],
        borderColor: [
          'rgba(52, 152, 219, 1)',
          'rgba(46, 204, 113, 1)'
        ],
        borderWidth: 1,
      },
    ],
  };

  // Chart data for overdue components by ship
  const overdueChartData = {
    labels: ['Overdue', 'On Time'],
    datasets: [
      {
        data: [data.overdueComponents, data.totalShips - data.overdueComponents],
        backgroundColor: [
          'rgba(231, 76, 60, 0.7)',
          'rgba(46, 204, 113, 0.7)'
        ],
        borderColor: [
          'rgba(231, 76, 60, 1)',
          'rgba(46, 204, 113, 1)'
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="kpi-dashboard">
      <h1>Maintenance KPIs Dashboard</h1>
      
      <div className="kpi-cards">
        {/* Total Ships Card */}
        <div className="kpi-card">
          <div className="card-icon" style={{ backgroundColor: '#3498db20' }}>
            <FaShip style={{ color: '#3498db' }} />
          </div>
          <div className="card-content">
            <h3>Total Ships</h3>
            <p className="card-value">{data.totalShips}</p>
            <p className="card-description">Active vessels in fleet</p>
          </div>
        </div>

        {/* Overdue Components Card */}
        <div className="kpi-card">
          <div className="card-icon" style={{ backgroundColor: '#e74c3c20' }}>
            <FaExclamationTriangle style={{ color: '#e74c3c' }} />
          </div>
          <div className="card-content">
            <h3>Overdue Maintenance</h3>
            <p className="card-value">{data.overdueComponents}</p>
            <p className="card-description">Components needing attention</p>
          </div>
        </div>

        {/* Jobs in Progress Card */}
        <div className="kpi-card">
          <div className="card-icon" style={{ backgroundColor: '#f39c1220' }}>
            <FaTasks style={{ color: '#f39c12' }} />
          </div>
          <div className="card-content">
            <h3>Jobs In Progress</h3>
            <p className="card-value">{data.jobsInProgress}</p>
            <p className="card-description">Active maintenance jobs</p>
          </div>
        </div>

        {/* Completed Jobs Card */}
        <div className="kpi-card">
          <div className="card-icon" style={{ backgroundColor: '#2ecc7020' }}>
            <FaCheckCircle style={{ color: '#2ecc70' }} />
          </div>
          <div className="card-content">
            <h3>Completed Jobs</h3>
            <p className="card-value">{data.jobsCompleted}</p>
            <p className="card-description">Jobs finished this period</p>
          </div>
        </div>
      </div>

      <div className="kpi-charts">
        <div className="chart-container">
          <h3>Jobs Status Distribution</h3>
          <div className="chart-wrapper">
            <Bar 
              data={jobsChartData}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                  y: {
                    beginAtZero: true,
                    ticks: {
                      stepSize: 1
                    }
                  }
                },
                plugins: {
                  legend: {
                    display: false
                  }
                }
              }}
            />
          </div>
        </div>

        <div className="chart-container">
          <h3>Maintenance Compliance</h3>
          <div className="chart-wrapper">
            <Pie 
              data={overdueChartData}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'bottom'
                  }
                }
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default KPIDashboard;
